/**
 * Admin App Configuration
 * FSD v2.1: App Layer - Config
 */

import { commonConfig } from '@shared';

const ensureString = (value: unknown, fallback: string): string => {
  if (typeof value === 'string' && value.length > 0) {
    return value;
  }
  return fallback;
};

const appUrlEnv: unknown = import.meta.env.VITE_APP_URL;

const accountsUrl = ensureString(commonConfig.urls.accounts, 'https://local.accounts.ddcn41.com');
const clientUrl = ensureString(commonConfig.urls.client, 'https://local.ddcn41.com');
const adminUrl = ensureString(commonConfig.urls.admin, 'https://local.admin.ddcn41.com');
const apiBase = ensureString(commonConfig.apiBase, 'https://api.ddcn41.com');
const cookieDomain = ensureString(commonConfig.cookieDomain, '.ddcn41.com');
const homeRoute = ensureString(commonConfig.routes.home, '/');
const accountsSelectRoute = ensureString(commonConfig.routes.accountsSelect, '/select');

export const appConfig = {
  // App-specific URL
  appUrl: ensureString(appUrlEnv, adminUrl),

  // Common URLs from shared config
  accountsUrl,
  clientUrl,

  // API from shared config
  apiBase,

  // Cookie from shared config
  cookieDomain,

  // Routes
  routes: {
    home: homeRoute,
    accountsSelect: accountsSelectRoute,
  },
} as const;

/**
 * Admin App Entry Point
 * FSD v2.1: app/index.tsx
 * Domain: admin.ddcn41.com
 */

import { BrowserRouter } from 'react-router-dom';
import { AdminAuthProvider } from './providers/AuthProvider';
import { AppRoutes } from './routes';

export function App() {
  return (
    <BrowserRouter>
      <AdminAuthProvider>
        <AppRoutes />
      </AdminAuthProvider>
    </BrowserRouter>
  );
}

export default App;

/**
 * Admin app Layout component
 * Uses AdminHeader widget and provides main layout structure
 */

import { Outlet } from 'react-router-dom';
import { Container } from '@packages/design-system';
import { AdminHeader } from '../../widgets/admin-header';
import { AdminSidebar } from '../../widgets/admin-sidebar';
import { appConfig } from '../config/config';

export function Layout() {
  return (
    <div className="flex min-h-screen flex-col bg-background">
      {/* Header Widget */}
      <AdminHeader />

      {/* Body with Sidebar and Main Content */}
      <div className="flex flex-1">
        {/* Sidebar Widget */}
        <AdminSidebar />

        {/* Main Content */}
        <main className="flex-1 overflow-y-auto py-8">
          <Container>
            <Outlet />
          </Container>
        </main>
      </div>

      {/* Footer */}
      <footer className="border-t bg-muted/40 py-4">
        <Container className="text-center text-sm text-muted-foreground">
          관리자 앱 - <code className="font-mono">{appConfig.appUrl.replace('https://', '')}</code>
        </Container>
      </footer>
    </div>
  );
}

/**
 * Admin app AuthProvider
 * Extends shared useAuthState with auto-redirect behavior
 */

import { createContext, useContext, useEffect, type ReactNode } from 'react';
import { useAuthState, commonConfig, type AuthContextValue } from '@shared';

const AdminAuthContext = createContext<AuthContextValue | undefined>(undefined);

interface AdminAuthProviderProps {
  readonly children: ReactNode;
}

export function AdminAuthProvider({ children }: AdminAuthProviderProps) {
  // Use shared auth state with admin-specific behavior
  const accountsLoginUrl = `${commonConfig.urls.accounts}${commonConfig.routes.accountsLogin}`;
  const logoutUri = typeof window !== 'undefined' ? window.location.origin : undefined;

  const { user, loading, loadUser, logout, refetchUser } = useAuthState({
    logoutUri,
    onUnauthenticated: () => {
      // Admin: auto-redirect to login when not authenticated
      console.warn('[Admin] Not authenticated - redirecting to login');
      window.location.href = accountsLoginUrl;
    },
    onAuthError: () => {
      // Admin: redirect to login on auth errors
      console.warn('[Admin] Auth error - redirecting to login');
      window.location.href = accountsLoginUrl;
    },
  });

  useEffect(() => {
    void loadUser();
  }, [loadUser]);

  return (
    <AdminAuthContext.Provider value={{ user, loading, logout, refetchUser }}>
      {children}
    </AdminAuthContext.Provider>
  );
}

export function useAdminAuth(): AuthContextValue {
  const context = useContext(AdminAuthContext);
  if (!context) {
    throw new Error('useAdminAuth must be used within AdminAuthProvider');
  }
  return context;
}

/**
 * Admin App Routes
 * FSD v2.1: app/routes/index.tsx
 */

import { Routes, Route, Navigate } from 'react-router-dom';
import { Layout } from '../layouts/Layout';
import { AdminDashboardPage } from '@/pages/admin-dashbaord';
import { AdminUsersPage } from '@/pages/admin-users';
import { AdminPerformancesPage } from '@/pages/admin-performances';
import { AdminBookingsPage } from '@/pages/admin-bookings';

export const AppRoutes = () => {
  return (
    <Routes>
      <Route element={<Layout />}>
        {/* Admin Routes */}
        <Route path="/" element={<AdminDashboardPage />} />
        <Route path="/users" element={<AdminUsersPage />} />
        <Route path="/performances" element={<AdminPerformancesPage />} />
        <Route path="/bookings" element={<AdminBookingsPage />} />

        {/* Fallback */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Route>
    </Routes>
  );
};

