export { authService } from '@shared/lib/auth';
export type { User, AuthResponse, LoginRequest, LogoutResponse } from '@shared/lib/auth';

import { apiClient } from '@shared/api';
import { API_CONFIG } from '@shared/config/api';
import type {
  BookingDto,
  BookingSeatDto,
  BookingSearchParams,
  CancelBooking200ResponseDto,
  CancelBookingRequestDto,
  CreateBookingRequestDto,
  CreateBookingResponseDto,
  GetBookingDetail200ResponseDto,
  GetBookings200ResponseDto,
} from '@packages/shared';

const buildSeatCode = (seat: BookingSeatDto): string => {
  const row = seat.rowLabel ?? '';
  const col = seat.colNum ?? '';
  return `${row}${col}`.trim();
};

const parseSeatCodeString = (seatCode?: string | null): string[] => {
  if (!seatCode) {
    return [];
  }
  return seatCode
    .split(',')
    .map((value) => value.trim())
    .filter((value) => value.length > 0);
};

const normalizeSeatCodes = (booking: BookingDto): string[] => {
  const seatCodesFromSeats = booking.seats?.map(buildSeatCode).filter(Boolean) ?? [];
  if (seatCodesFromSeats.length > 0) {
    return seatCodesFromSeats;
  }
  if (booking.seatCodes && booking.seatCodes.length > 0) {
    return booking.seatCodes;
  }
  return parseSeatCodeString(booking.seatCode);
};

const computeSeatCount = (booking: BookingDto): number => {
  if (typeof booking.seatCount === 'number' && booking.seatCount > 0) {
    return booking.seatCount;
  }
  return booking.seats?.length ?? 0;
};

const normalizeBooking = (booking: BookingDto): BookingDto => {
  const seatCodes = normalizeSeatCodes(booking);
  return {
    ...booking,
    seats: booking.seats ?? [],
    seatCodes,
    seatCode: seatCodes.length > 0 ? seatCodes.join(', ') : (booking.seatCode ?? ''),
    seatCount: computeSeatCount(booking),
  };
};

export const bookingService = {
  async getBookings(params?: BookingSearchParams): Promise<GetBookings200ResponseDto> {
    const searchParams = new URLSearchParams();
    if (params?.status) searchParams.append('status', params.status);
    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.limit) searchParams.append('limit', params.limit.toString());

    const query = searchParams.toString();
    const baseEndpoint = `${API_CONFIG.ENDPOINTS.BOOKINGS}/me`;
    const url = query.length > 0 ? `${baseEndpoint}?${query}` : baseEndpoint;
    const resp = await apiClient.get<GetBookings200ResponseDto>(url);

    // Normalize each booking to ensure seats/seatCodes/seatCode/seatCount are consistent
    return {
      ...resp,
      bookings: resp.bookings.map(normalizeBooking),
    };
  },

  async createBooking(
    bookingData: CreateBookingRequestDto,
    // queueToken?: string
  ): Promise<CreateBookingResponseDto> {
    return apiClient.post<CreateBookingResponseDto>(API_CONFIG.ENDPOINTS.BOOKINGS, bookingData);
  },

  async getBookingDetail(bookingId: number): Promise<GetBookingDetail200ResponseDto> {
    const resp = await apiClient.get<GetBookingDetail200ResponseDto>(
      `${API_CONFIG.ENDPOINTS.BOOKINGS}/${bookingId}`,
    );

    return normalizeBooking(resp);
  },

  async cancelBooking(
    bookingId: number,
    request?: CancelBookingRequestDto,
  ): Promise<CancelBooking200ResponseDto> {
    return apiClient.patch<CancelBooking200ResponseDto>(
      `${API_CONFIG.ENDPOINTS.BOOKINGS}/${bookingId}/cancel`,
      request,
    );
  },

  async adminGetBookings(params?: BookingSearchParams): Promise<GetBookings200ResponseDto> {
    const searchParams = new URLSearchParams();
    if (params?.status) searchParams.append('status', params.status);
    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.limit) searchParams.append('limit', params.limit.toString());

    const query = searchParams.toString();
    const baseEndpoint = API_CONFIG.ENDPOINTS.ADMIN_BOOKINGS;
    const url = query.length > 0 ? `${baseEndpoint}?${query}` : baseEndpoint;
    return apiClient.get<GetBookings200ResponseDto>(url);
  },

  async adminGetBookingDetail(bookingId: number): Promise<GetBookingDetail200ResponseDto> {
    const response = await apiClient.get<GetBookingDetail200ResponseDto>(
      `${API_CONFIG.ENDPOINTS.ADMIN_BOOKINGS}/${bookingId}`,
    );
    return normalizeBooking(response);
  },
};

