  async deleteUser(userId: number): Promise<boolean> {
    try {
      await apiClient.delete(`${API_CONFIG.ENDPOINTS.USERS}/${userId}`);
      return true;
    } catch (error) {
      console.error('Failed to delete user:', error);
      return false;
    }
