// admin booking data transform util function
const transformAdminBookingData = (
  response: BookingDto,
  userMap: Map<number, User>,
): AdminBooking => {
  const user = userMap.get(response.userId);

  const transformed: AdminBooking = {
    booking_id: response.bookingId,
    booking_number: response.bookingNumber,
    user_id: response.userId,
    performance_id: response.scheduleId,
    performance_title: response.performanceTitle,
    venue_name: response.venueName,
    show_datetime: response.showDate,
    seat_count: response.seatCount,
    total_amount: response.totalAmount,
    status: response.status,
    booked_at: response.bookedAt,
    cancelled_at: response.cancelledAt,
    cancellation_reason: response.cancellationReason,
    seats: response.seats,
    user_name: user?.name || 'Unknown User',
    user_email: user?.email || 'unknown@email.com',
    payment_status: (response.status === 'CONFIRMED' ? 'COMPLETED' : 'FAILED') as
      | 'PENDING'
      | 'COMPLETED'
      | 'FAILED',
  };

  return transformed;
};

export default function AdminBookingsPage({
  permissions = { canViewBookings: true },
}: BookingManagementProps) {
  const [bookings, setBookings] = useState<AdminBooking[]>([]);
  const [filteredBookings, setFilteredBookings] = useState<AdminBooking[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<'all' | 'CONFIRMED' | 'CANCELLED'>('all');
  const [paymentFilter, setPaymentFilter] = useState<string>('all');
  const [selectedBooking, setSelectedBooking] = useState<AdminBooking | null>(null);

  const searchBookings = async (searchParams?: {
    status?: string;
    page?: number;
    limit?: number;
  }): Promise<AdminBooking[]> => {
    try {
      const bookingsData = await bookingService.adminGetBookings({
        status: searchParams?.status === 'all' ? '' : searchParams?.status || '',
        page: searchParams?.page || 1,
        limit: searchParams?.limit || 20,
      });
      const userData = await userService.getUsers();
      const userMap = new Map(userData.map((u) => [u.user_id, u]));

      // Convert Booking to AdminBooking by adding user data
      const adminBookings: AdminBooking[] = bookingsData.bookings.map((booking) =>
        transformAdminBookingData(booking, userMap),
      );

      return adminBookings;
    } catch (error) {
      console.error('Failed to search bookings: ', error);
      return [];
    }
  };

  const filterBookingsBySearchTerm = (
    bookings: AdminBooking[],
    searchTerm: string,
  ): AdminBooking[] => {
    if (!searchTerm) return bookings;

    return bookings.filter(
      (booking) =>
        booking.booking_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
        booking.user_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        booking.user_email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (booking.performance_title || '').toLowerCase().includes(searchTerm.toLowerCase()),
    );
  };

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const adminBookings: AdminBooking[] = await searchBookings();

        setBookings(adminBookings);
        setFilteredBookings(adminBookings);
      } catch (error) {
        console.error('Failed to fetch bookings:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  useEffect(() => {
    const handleBookingFilter = async () => {
      if (statusFilter !== 'all') {
        try {
          const serverFilteredBookings = await searchBookings({ status: statusFilter });

          const finalFilteredBookings = filterBookingsBySearchTerm(
            serverFilteredBookings,
            searchTerm,
          );

          setFilteredBookings(finalFilteredBookings);
        } catch (error) {
          console.error('Failed to filter bookings:', error);
          setFilteredBookings([]);
        } finally {
        }
        return;
      }

      const localFilteredBookings = filterBookingsBySearchTerm(bookings, searchTerm);
      setFilteredBookings(localFilteredBookings);
    };

    handleBookingFilter();
  }, [bookings, searchTerm, statusFilter]);

  const handleCancelBooking = (bookingId: number) => {
    if (window.confirm('Are you sure you want to cancel this booking?')) {
      setBookings((prev) =>
        prev.map((booking) =>
          booking.booking_id === bookingId ? { ...booking, status: 'CANCELLED' as const } : booking,
        ),
      );
    }
  };

  const handleConfirmBooking = (bookingId: number) => {
    setBookings((prev) =>
      prev.map((booking) =>
        booking.booking_id === bookingId
          ? { ...booking, status: 'CONFIRMED' as const, payment_status: 'COMPLETED' as const }
          : booking,
      ),
    );
  };

  const exportToCSV = () => {
    const csvData = filteredBookings.map((booking) => ({
      'Booking Number': booking.booking_number,
      'Customer Name': booking.user_name,
      Email: booking.user_email,
      Performance: booking.performance_title,
      Venue: booking.venue_name,
      'Show Date': formatDate(booking.show_datetime),
      Seats: booking.seat_count,
      Amount: booking.total_amount,
      Status: booking.status,
      Payment: booking.payment_status,
      'Booked At': formatDate(booking.booked_at),
    }));

    const csvContent = [
      Object.keys(csvData[0]).join(','),
      ...csvData.map((row) => Object.values(row).join(',')),
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `bookings-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'CONFIRMED':
        return 'default';
      case 'CANCELLED':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const getPaymentStatusColor = (status: string) => {
    switch (status) {
      case 'COMPLETED':
        return 'default';
      case 'PENDING':
        return 'secondary';
      case 'FAILED':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const formatDate = (dateString: string | undefined) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('ko-KR').format(price) + '원';
  };

  if (!permissions.canViewBookings) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <p className="text-muted-foreground">
            Access denied. You don't have permission to view booking management.
          </p>
        </CardContent>
      </Card>
    );
  }

  if (loading) {
    return (
      <div className="space-y-4">
        <Card className="animate-pulse">
          <CardContent className="p-6">
            <div className="h-8 bg-muted rounded w-1/3 mb-4"></div>
            <div className="space-y-2">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-4 bg-muted rounded"></div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2>Booking Management</h2>
          <p className="text-sm text-muted-foreground">
            Monitor and manage customer bookings and payments
          </p>
        </div>
        <Button onClick={exportToCSV} variant="outline">
          <Download className="w-4 h-4 mr-2" />
          Export CSV
        </Button>
      </div>

      {/* Booking Stats */}
      <div className="grid gap-4 md:grid-cols-3">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <div>
                <p className="text-sm text-muted-foreground">Confirmed</p>
                <p className="text-xl font-medium">
                  {bookings.filter((b) => b.status === 'CONFIRMED').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <XCircle className="w-5 h-5 text-red-500" />
              <div>
                <p className="text-sm text-muted-foreground">Cancelled</p>
                <p className="text-xl font-medium">
                  {bookings.filter((b) => b.status === 'CANCELLED').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <div className="w-5 h-5 bg-blue-500 rounded"></div>
              <div>
                <p className="text-sm text-muted-foreground">Total Revenue</p>
                <p className="text-xl font-medium">
                  {formatPrice(
                    bookings
                      .filter((b) => b.status === 'CONFIRMED')
                      .reduce((sum, b) => sum + b.total_amount, 0),
                  )}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-wrap gap-4">
            <div className="flex items-center gap-2">
              <Search className="w-4 h-4" />
              <Input
                placeholder="Search bookings..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-64"
              />
            </div>

            <Select
              value={statusFilter}
              onValueChange={(value) => setStatusFilter(value as 'all' | 'CONFIRMED' | 'CANCELLED')}
            >
              <SelectTrigger className="w-40">
                <Filter className="w-4 h-4 mr-2" />
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="CONFIRMED">Confirmed</SelectItem>
                <SelectItem value="CANCELLED">Cancelled</SelectItem>
              </SelectContent>
            </Select>

            <Select value={paymentFilter} onValueChange={setPaymentFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Payment" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Payments</SelectItem>
                <SelectItem value="COMPLETED">Completed</SelectItem>
                <SelectItem value="PENDING">Pending</SelectItem>
                <SelectItem value="FAILED">Failed</SelectItem>
              </SelectContent>
            </Select>

            <div className="text-sm text-muted-foreground flex items-center">
              Showing {filteredBookings.length} of {bookings.length} bookings
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Bookings Table */}
      <Card>
        <CardHeader>
          <CardTitle>All Bookings</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Booking #</TableHead>
                <TableHead>Customer</TableHead>
                <TableHead>Performance</TableHead>
                <TableHead>Show Date</TableHead>
                <TableHead>Seats</TableHead>
                <TableHead>Amount</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Payment</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredBookings.map((booking) => (
                <TableRow key={booking.booking_id}>
                  <TableCell>
                    <div className="font-medium">{booking.booking_number}</div>
                    <div className="text-xs text-muted-foreground">
                      {formatDate(booking.booked_at)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="font-medium">{booking.user_name}</div>
                    <div className="text-xs text-muted-foreground">{booking.user_email}</div>
                  </TableCell>
                  <TableCell>
                    <div className="font-medium">{booking.performance_title}</div>
                    <div className="text-xs text-muted-foreground">{booking.venue_name}</div>
                  </TableCell>
                  <TableCell>{formatDate(booking.show_datetime)}</TableCell>
                  <TableCell>
                    <div className="font-medium">{booking.seat_count} seats</div>
                    <div className="text-xs text-muted-foreground">
                      {(booking.seats || []).map((s) => `${s.rowLabel}${s.colNum}`).join(', ')}
                    </div>
                  </TableCell>
                  <TableCell>{formatPrice(booking.total_amount)}</TableCell>
                  <TableCell>
                    <Badge variant={getStatusColor(booking.status)}>{booking.status}</Badge>
                  </TableCell>
                  <TableCell>
                    <Badge variant={getPaymentStatusColor(booking.payment_status)}>
                      {booking.payment_status}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex gap-1">
                      <Dialog>
                        <DialogTrigger asChild>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => setSelectedBooking(booking)}
                          >
                            <Eye className="w-4 h-4" />
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="max-w-2xl">
                          <DialogHeader>
                            <DialogTitle>Booking Details</DialogTitle>
                          </DialogHeader>
                          {selectedBooking && (
                            <div className="space-y-4">
                              <div className="grid grid-cols-2 gap-4">
                                <div>
                                  <p className="text-sm text-muted-foreground">Booking Number</p>
                                  <p className="font-medium">{selectedBooking.booking_number}</p>
                                </div>
                                <div>
                                  <p className="text-sm text-muted-foreground">Status</p>
                                  <Badge variant={getStatusColor(selectedBooking.status)}>
                                    {selectedBooking.status}
                                  </Badge>
                                </div>
                                <div>
                                  <p className="text-sm text-muted-foreground">Customer</p>
                                  <p className="font-medium">{selectedBooking.user_name}</p>
                                  <p className="text-sm text-muted-foreground">
                                    {selectedBooking.user_email}
                                  </p>
                                </div>
                                <div>
                                  <p className="text-sm text-muted-foreground">Payment Status</p>
                                  <Badge
                                    variant={getPaymentStatusColor(selectedBooking.payment_status)}
                                  >
                                    {selectedBooking.payment_status}
                                  </Badge>
                                </div>
                              </div>

                              <div>
                                <p className="text-sm text-muted-foreground mb-2">Seat Details</p>
                                <div className="bg-muted rounded-lg p-3">
                                  {(selectedBooking.seats || []).map((seat, index) => (
                                    <div key={index} className="flex justify-between text-sm">
                                      <span>
                                        {seat.rowLabel}
                                        {seat.colNum} ({seat.grade})
                                      </span>
                                      <span>{formatPrice(seat.seatPrice)}</span>
                                    </div>
                                  ))}
                                  <div className="border-t mt-2 pt-2 flex justify-between font-medium">
                                    <span>Total</span>
                                    <span>{formatPrice(selectedBooking.total_amount)}</span>
                                  </div>
                                </div>
                              </div>
                            </div>
                          )}
                        </DialogContent>
                      </Dialog>

                      {booking.status !== 'CANCELLED' && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleCancelBooking(booking.booking_id)}
                        >
                          <XCircle className="w-4 h-4" />
                        </Button>
                      )}
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import {
  SystemOverview,
  PerformanceManagement,
  UserManagement,
  BookingManagement,
  TrafficControl,
  AsgDashboard,
} from '@/widgets/admin-dashboard';
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
  Card,
  CardHeader,
  CardContent,
  CardTitle,
  Badge,
} from '@packages/design-system';

import { useAdminAuth } from '@/app/providers/AuthProvider';
import type { User as SharedUser } from '@shared/lib/auth';
import type { User } from '@/entities/user';

interface AdminDashboardProps {
  user?: SharedUser;
}

export default function AdminDashboardPage({ user: propUser }: AdminDashboardProps) {
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  const [activeTab, setActiveTab] = useState('overview');
  const { user: authUser } = useAdminAuth();

  const currentUser = propUser || authUser;
  const user: User | null = currentUser
    ? {
        user_id: currentUser.userId,
        email: currentUser.email,
        username: currentUser.username,
        name: currentUser.name,
        role: currentUser.role,
        created_at: currentUser.created_at || new Date().toISOString(),
        last_login: currentUser.lastLogin || currentUser.last_login,
      }
    : null;

  const getRolePermissions = (role: string) => {
    switch (role) {
      case 'ADMIN':
        return {
          canManageUsers: true,
          canManagePerformances: true,
          canViewBookings: true,
          canControlTraffic: false,
          canViewMetrics: false,
          canScaleInfrastructure: false,
        };
      case 'DEVOPS':
        return {
          canManageUsers: true,
          canManagePerformances: false,
          canViewBookings: false,
          canControlTraffic: true,
          canViewMetrics: true,
          canScaleInfrastructure: true,
        };
      case 'DEV':
        return {
          canManageUsers: false,
          canManagePerformances: false,
          canViewBookings: false,
          canControlTraffic: false,
          canViewMetrics: true,
          canScaleInfrastructure: false,
        };
      default:
        return {
          canManageUsers: false,
          canManagePerformances: false,
          canViewBookings: false,
          canControlTraffic: false,
          canViewMetrics: false,
          canScaleInfrastructure: false,
        };
    }
  };

  const permissions = user
    ? getRolePermissions(user.role)
    : {
        canManageUsers: false,
        canManagePerformances: false,
        canViewBookings: false,
        canControlTraffic: false,
        canViewMetrics: false,
        canScaleInfrastructure: false,
      };

  const getTabList = () => {
    const tabs = [
      { value: 'overview', label: 'Dashboard', condition: permissions.canViewMetrics },
      {
        value: 'performances',
        label: 'Performances',
        condition: permissions.canManagePerformances,
      },
      { value: 'bookings', label: 'Bookings', condition: permissions.canViewBookings },
      { value: 'users', label: 'Users', condition: permissions.canManageUsers },
      { value: 'traffic', label: 'Traffic Control', condition: permissions.canControlTraffic },
      { value: 'asg', label: 'ASG Dashboard', condition: permissions.canScaleInfrastructure },
    ];

    return tabs.filter((tab) => tab.condition);
  };

  const availableTabs = getTabList();

  // Set initial tab and read from URL params
  useEffect(() => {
    const tab = searchParams.get('tab');
    if (tab && availableTabs.some((t) => t.value === tab)) {
      setActiveTab(tab);
    } else if (availableTabs.length > 0) {
      setActiveTab(availableTabs[0].value);
    }
  }, [searchParams, availableTabs]);

  // Early return if no user - AFTER all hooks
  if (!user) {
    return null;
  }

  const handleTabChange = (value: string) => {
    setActiveTab(value);
    setSearchParams({ tab: value });
  };

  // Debug logging
  console.log(`User:`, user, 'Permissions:', permissions, 'Available tabs:', availableTabs);

  return (
    <div className="container mx-auto px-4 py-6 space-y-6">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Admin Portal</CardTitle>
              <p className="text-sm text-muted-foreground mt-1">
                {availableTabs.length} tab{availableTabs.length !== 1 ? 's' : ''} available for{' '}
                {user.role}
              </p>
            </div>
            <Badge variant="secondary">{user.role}</Badge>
          </div>
        </CardHeader>
        <CardContent>
          <Tabs value={activeTab} onValueChange={handleTabChange}>
            <TabsList
              className="grid w-full"
              style={{ gridTemplateColumns: `repeat(${availableTabs.length}, 1fr)` }}
            >
              {availableTabs.map((tab) => (
                <TabsTrigger key={tab.value} value={tab.value}>
                  {tab.label}
                </TabsTrigger>
              ))}
            </TabsList>

            {permissions.canViewMetrics && (
              <TabsContent value="overview" className="space-y-4">
                <SystemOverview permissions={permissions} />
              </TabsContent>
            )}

            {permissions.canManagePerformances && (
              <TabsContent value="performances" className="space-y-4">
                <PerformanceManagement />
              </TabsContent>
            )}

            {permissions.canViewBookings && (
              <TabsContent value="bookings" className="space-y-4">
                <BookingManagement permissions={permissions} />
              </TabsContent>
            )}

            {permissions.canManageUsers && (
              <TabsContent value="users" className="space-y-4">
                <UserManagement />
              </TabsContent>
            )}

            {permissions.canControlTraffic && (
              <TabsContent value="traffic" className="space-y-4">
                <TrafficControl permissions={permissions} />
              </TabsContent>
            )}

            {permissions.canScaleInfrastructure && (
              <TabsContent value="asg" className="space-y-4">
                <AsgDashboard />
              </TabsContent>
            )}
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

