/**
 * Admin Permission Utilities
 * Role-based access control helpers
 */

export type UserRole = 'USER' | 'ADMIN' | 'DEV' | 'DEVOPS' | string;

export interface PermissionConfig {
  roles: UserRole[];
  requireAll?: boolean; // true면 모든 role 필요, false면 하나만 있어도 됨 (기본: false)
}

/**
 * Check if user has required role(s)
 */
export function hasRole(userRole: UserRole | undefined, allowedRoles: UserRole[]): boolean {
  if (!userRole) return false;
  return allowedRoles.includes(userRole);
}

/**
 * Check if user has permission based on role
 */
export function hasPermission(userRole: UserRole | undefined, config: PermissionConfig): boolean {
  if (!userRole) return false;

  if (config.requireAll) {
    // 모든 role이 필요한 경우
    return config.roles.every((role) => userRole === role);
  } else {
    // 하나의 role만 있으면 되는 경우 (기본)
    return config.roles.includes(userRole);
  }
}

/**
 * Admin permissions map
 */
export const ADMIN_PERMISSIONS = {
  // 대시보드 - 모든 관리자
  viewDashboard: { roles: ['ADMIN', 'DEV', 'DEVOPS'] },

  // 사용자 관리 - ADMIN만
  viewUsers: { roles: ['ADMIN'] },
  createUser: { roles: ['ADMIN'] },
  updateUser: { roles: ['ADMIN'] },
  deleteUser: { roles: ['ADMIN'] },

  // 공연 관리 - ADMIN, DEV
  viewPerformances: { roles: ['ADMIN', 'DEV'] },
  createPerformance: { roles: ['ADMIN', 'DEV'] },
  updatePerformance: { roles: ['ADMIN', 'DEV'] },
  deletePerformance: { roles: ['ADMIN', 'DEV'] },

  // 예매 관리 - ADMIN, DEV
  viewBookings: { roles: ['ADMIN', 'DEV'] },
  cancelBooking: { roles: ['ADMIN', 'DEV'] },

  // 시스템 관리 - ADMIN, DEVOPS
  viewSystem: { roles: ['ADMIN', 'DEVOPS'] },
  manageInfrastructure: { roles: ['DEVOPS'] },
} as const;

/**
 * Check if user can access a specific permission
 */
export function canAccess(
  userRole: UserRole | undefined,
  permission: keyof typeof ADMIN_PERMISSIONS,
): boolean {
  const config = ADMIN_PERMISSIONS[permission];
  return hasPermission(userRole, config);
}

/**
 * Permission Guard Component
 * Protects routes based on user permissions
 */

import type { ReactNode } from 'react';
import { Navigate } from 'react-router-dom';
import { Card, CardContent } from '@packages/design-system/ui/card';
import { Skeleton } from '@packages/design-system/ui/skeleton';
import { useAdminAuth } from '@/app/providers/AuthProvider';
import type { ADMIN_PERMISSIONS } from '@/shared/lib/permissions';
import { canAccess } from '@/shared/lib/permissions';

interface PermissionGuardProps {
  children: ReactNode;
  permission: keyof typeof ADMIN_PERMISSIONS;
  redirectTo?: string;
  showMessage?: boolean;
}

export function PermissionGuard({
  children,
  permission,
  redirectTo = '/',
  showMessage = true,
}: PermissionGuardProps) {
  const { user, loading } = useAdminAuth();

  // Show loading state
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="space-y-4 w-full max-w-md">
          <Skeleton className="h-8 w-full" />
          <Skeleton className="h-32 w-full" />
        </div>
      </div>
    );
  }

  // Check permission
  const hasAccess = canAccess(user?.role, permission);

  if (!hasAccess) {
    if (showMessage) {
      return (
        <Card>
          <CardContent className="p-8 text-center">
            <div className="space-y-4">
              <div className="text-4xl">🔒</div>
              <div>
                <h3 className="text-lg font-semibold mb-2">접근 권한이 없습니다</h3>
                <p className="text-sm text-muted-foreground">
                  이 페이지에 접근할 수 있는 권한이 없습니다.
                </p>
                <p className="text-xs text-muted-foreground mt-2">
                  현재 Role:{' '}
                  <code className="bg-muted px-2 py-1 rounded">{user?.role || 'UNKNOWN'}</code>
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      );
    }
    return <Navigate to={redirectTo} replace />;
  }

  return <>{children}</>;
}

