                  {instances.map((instance) => (
                    <TableRow key={instance.instanceId}>
                      <TableCell className="font-mono text-xs">{instance.instanceId}</TableCell>
                      <TableCell className="text-sm">
                        <Badge variant="outline">{instance.lifecycleState}</Badge>
                      </TableCell>
                      <TableCell>
