source:
import React, { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  CardTitle,
  Button,
  Input,
  Label,
  Textarea,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Badge,
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@packages/design-system';

import { Plus, Edit, Trash2, Calendar, MapPin, Users } from 'lucide-react';
import type { Performance, PerformanceRequest, Venue } from '@/entities/performance';
import type { VenueDto } from '@packages/shared';
import { adminPerformanceService } from '@/entities/performance';
import { venueService } from '@/entities/venue';

const transformVenueDto = (venue: VenueDto): Venue => ({
  venue_id: venue.venueId,
  venue_name: venue.venueName,
  address: venue.address,
  description: venue.description ?? '',
  contact: venue.contact ?? '',
  total_capacity: venue.totalCapacity,
  created_at: '',
});

// PerformanceForm을 별도 컴포넌트로 분리
function PerformanceForm({
  formData,
  setFormData,
  venues,
  onSubmit,
  onCancel,
  isEdit = false,
}: {
  formData: any;
  setFormData: (data: any) => void;
  venues: Venue[];
  onSubmit: () => void;
  onCancel: () => void;
  isEdit?: boolean;
}) {
  return (
    <div className="space-y-4">
      <div>
        <Label htmlFor="title">Title</Label>
        <Input
          id="title"
          value={formData.title}
          onChange={(e) => setFormData((prev: any) => ({ ...prev, title: e.target.value }))}
          placeholder="Performance title"
        />
      </div>

      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={formData.description}
          onChange={(e) => setFormData((prev: any) => ({ ...prev, description: e.target.value }))}
          placeholder="Performance description"
          rows={3}
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="theme">Theme</Label>
          <Select
            value={formData.theme}
            onValueChange={(value: any) => setFormData((prev: any) => ({ ...prev, theme: value }))}
          >
            <SelectTrigger>
              <SelectValue placeholder="Select theme" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="Musical">Musical</SelectItem>
              <SelectItem value="Ballet">Ballet</SelectItem>
              <SelectItem value="Concert">Concert</SelectItem>
              <SelectItem value="Opera">Opera</SelectItem>
              <SelectItem value="Theater">Theater</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="venue">Venue</Label>
          <Select
            value={formData.venue_id.toString()}
            onValueChange={(value: string) =>
              setFormData((prev: any) => ({ ...prev, venue_id: parseInt(value) }))
            }
          >
            <SelectTrigger>
              <SelectValue placeholder="Select venue" />
            </SelectTrigger>
            <SelectContent>
              {venues.map((venue) => (
                <SelectItem key={venue.venue_id} value={venue.venue_id.toString()}>
                  {venue.venue_name} ({venue.total_capacity} seats)
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div>
        <Label htmlFor="poster_url">Poster Image</Label>
        <div className="flex items-center gap-2">
          <Button
            type="button"
            variant="outline"
            onClick={() => document.getElementById('poster-file-input')?.click()}
            className="w-fit"
          >
            Choose File
          </Button>
          <span className="text-sm text-gray-500">
            {formData.poster_image?.name || 'No file selected'}
          </span>
        </div>
        <input
          id="poster-file-input"
          type="file"
          accept="image/*"
          className="hidden"
          onChange={(e) =>
            setFormData((prev: any) => ({ ...prev, poster_image: e.target.files?.[0] || null }))
          }
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="start_date">Start Date</Label>
          <Input
            id="start_date"
            type="date"
            value={formData.start_date}
            onChange={(e) => setFormData((prev: any) => ({ ...prev, start_date: e.target.value }))}
          />
        </div>

        <div>
          <Label htmlFor="end_date">End Date</Label>
          <Input
            id="end_date"
            type="date"
            value={formData.end_date}
            onChange={(e) => setFormData((prev: any) => ({ ...prev, end_date: e.target.value }))}
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="running_time">Running Time (minutes)</Label>
          <Input
            id="running_time"
            type="number"
            value={formData.running_time}
            onChange={(e) =>
              setFormData((prev: any) => ({ ...prev, running_time: parseInt(e.target.value) || 0 }))
            }
            placeholder="120"
          />
        </div>

        <div>
          <Label htmlFor="base_price">Base Price (KRW)</Label>
          <Input
            id="base_price"
            type="number"
            value={formData.base_price}
            onChange={(e) =>
              setFormData((prev: any) => ({ ...prev, base_price: parseInt(e.target.value) || 0 }))
            }
            placeholder="50000"
          />
        </div>
      </div>

      <div className="flex justify-end gap-2 pt-4">
        <Button variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button onClick={onSubmit}>{isEdit ? 'Update' : 'Create'} Performance</Button>
      </div>
    </div>
  );
}

function ScheduleForm({
  formData,
  setFormData,
  performances,
  onSubmit,
  onCancel,
  isEdit = false,
}: {
  formData: any;
  setFormData: (data: any) => void;
  performances: Performance[];
  onSubmit: () => void;
  onCancel: () => void;
  isEdit?: boolean;
}) {
  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="performance">Performance</Label>
          <Select
            value={formData.performance?.performance_id?.toString() || ''}
            onValueChange={(value: string) => {
              const selectPerformance = performances.find(
                (p) => p.performance_id === parseInt(value),
              );
              setFormData((prev: any) => ({ ...prev, performance: selectPerformance }));
            }}
          >
            <SelectTrigger>
              <SelectValue placeholder="Select Performance" />
            </SelectTrigger>
            <SelectContent>
              {performances.map((performance) => (
                <SelectItem
                  key={performance.performance_id}
                  value={performance.performance_id.toString()}
                >
                  {performance.title}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="status">Status</Label>
          <Select
            value={formData.status}
            onValueChange={(value: any) => setFormData((prev: any) => ({ ...prev, status: value }))}
          >
            <SelectTrigger>
              <SelectValue placeholder="Select Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="OPEN">OPEN</SelectItem>
              <SelectItem value="CLOSED">CLOSED</SelectItem>
              <SelectItem value="SOLDOUT">SOLDOUT</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="show_datetime">Show Date time</Label>
          <Input
            id="show_datetime"
            type="datetime-local"
            value={formData.show_datetime || ''}
            onChange={(e) =>
              setFormData((prev: any) => ({ ...prev, show_datetime: e.target.value }))
            }
          />
        </div>
      </div>

      <div className="flex justify-end gap-2 pt-4">
        <Button variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button onClick={onSubmit}>{isEdit ? 'Update' : 'Create'} Schedule</Button>
      </div>
    </div>
  );
}

export function PerformanceManagement() {
  const [performances, setPerformances] = useState<Performance[]>([]);
  const [venues, setVenues] = useState<Venue[]>([]);
  const [initialLoading, setInitialLoading] = useState(true);
  const [editingPerformance, setEditingPerformance] = useState<Performance | null>(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showCreateScheduleDialog, setShowScheduleCreateDialog] = useState(false);
  const [formData, setFormData] = useState<{
    title: string;
    description: string;
    theme: string;
    poster_url: string;
    poster_image: File | null;
    start_date: string;
    end_date: string;
    running_time: number;
    base_price: number;
    venue_id: number;
  }>({
    title: '',
    description: '',
    theme: '',
    poster_url: '',
    poster_image: null,
    start_date: '',
    end_date: '',
    running_time: 0,
    base_price: 0,
    venue_id: 0,
  });

  const [scheduleFormData, setScheduleFormData] = useState<{
    performance: Performance;
    show_datetime: string;
    status: string;
  }>({
    performance: null,
    show_datetime: '',
    status: '',
  });

  // 초기화
  useEffect(() => {
    const fetchPerfomances = async () => {
      try {
        setInitialLoading(true);

        const venueData = await venueService.getAllVenues();
        const performanceData = await adminPerformanceService.getPerformances();

        setVenues(venueData.map(transformVenueDto));
        setPerformances(performanceData);
      } catch (error) {
        console.error('공연 데이터를 가져오는데 실패했습니다: ', error);
      } finally {
        setInitialLoading(false);
      }
    };

    fetchPerfomances();
  }, []);

  const handleCreatePerformance = async () => {
    try {
      const newPerformance = await adminPerformanceService.createPerformance(
        {
          venueId: formData.venue_id,
          title: formData.title,
          description: formData.description,
          theme: formData.theme,
          posterUrl: '',
          basePrice: formData.base_price,
          startDate: formData.start_date,
          endDate: formData.end_date,
          runningTime: formData.running_time,
          status: 'UPCOMING',
          schedules: [],
        },
        formData.poster_image,
      );

      if (newPerformance !== undefined) {
        setPerformances((prev) => [...prev, newPerformance]);
        setShowCreateDialog(false);
        resetForm();
        console.log('공연 생성 성공');
      } else {
        throw new Error('공연 생성 실패');
      }
    } catch (error) {
      console.error('공연 생성 실패: ', error);
    }
  };

  const handleUpdatePerformance = async () => {
    if (!editingPerformance) return;

    try {
      const updateRequestBody: PerformanceRequest = {
        venueId: formData.venue_id,
        title: formData.title,
        description: formData.description || '',
        theme: formData.theme,
        posterUrl: '',
        basePrice: formData.base_price,
        startDate: formData.start_date,
        endDate: formData.end_date,
        runningTime: formData.running_time,
        status: editingPerformance.status,
        schedules: [],
      };

      const updatedPerformance = await adminPerformanceService.updatePerformance(
        editingPerformance.performance_id,
        updateRequestBody,
        formData.poster_image,
      );

      if (updatedPerformance !== undefined) {
        setPerformances((prev) =>
          prev.map((perf) =>
            perf.performance_id === updatedPerformance.performance_id ? updatedPerformance : perf,
          ),
        );

        setEditingPerformance(null);
        resetForm();
        console.log('공연 수정 성공');
      } else {
        throw new Error('공연 수정 실패');
      }
    } catch (error) {
      console.error('공연 수정 실패: ', error);
    }
  };

  const handleDeletePerformance = async (id: number) => {
    if (!window.confirm('Are you sure you want to delete this performance?')) {
      return;
    }

    try {
      const success = await adminPerformanceService.deletePerformance(id);

      if (success) {
        setPerformances((prev) => prev.filter((perf) => perf.performance_id !== id));
        console.log('공연 삭제 성공');
      } else {
        throw new Error('공연 삭제 실패');
      }
    } catch (error) {
      console.error('공연 삭제 실패: ', error);
    }
  };

  const handleEditPerformance = (performance: Performance) => {
    setEditingPerformance(performance);
    setFormData({
      title: performance.title,
      description: performance.description || '',
      theme: performance.theme,
      poster_url: performance.poster_url,
      poster_image: null,
      start_date: performance.start_date,
      end_date: performance.end_date,
      running_time: performance.running_time,
      base_price: performance.base_price,
      venue_id: performance.venue_id,
    });
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      theme: '',
      poster_url: '',
      poster_image: null,
      start_date: '',
      end_date: '',
      running_time: 0,
      base_price: 0,
      venue_id: 0,
    });
  };

  const handleFormCancel = () => {
    setShowCreateDialog(false);
    setEditingPerformance(null);
    resetForm();
  };

  const handleCreateSchedule = async () => {
    try {
      const updateRequestBody: PerformanceRequest = {
        venueId: scheduleFormData.performance.venue_id,
        title: scheduleFormData.performance.title,
        description: scheduleFormData.performance.description || '',
        theme: scheduleFormData.performance.theme,
        posterUrl: '',
        basePrice: scheduleFormData.performance.base_price,
        startDate: scheduleFormData.performance.start_date,
        endDate: scheduleFormData.performance.end_date,
        runningTime: scheduleFormData.performance.running_time,
        status: scheduleFormData.performance.status,
        schedules: [
          {
            showDatetime: scheduleFormData.show_datetime,
            status: scheduleFormData.status,
          },
        ],
      };

      console.log(scheduleFormData);

      const updatedPerformance = await adminPerformanceService.updatePerformance(
        scheduleFormData.performance.performance_id,
        updateRequestBody,
        formData.poster_image,
      );

      if (updatedPerformance !== undefined) {
        setShowScheduleCreateDialog(false);

        setPerformances((prev) =>
          prev.map((perf) =>
            perf.performance_id === updatedPerformance.performance_id ? updatedPerformance : perf,
          ),
        );

        resetScheduleForm();
        console.log('스케줄 추가 성공');
      } else {
        throw new Error('스케줄 추가 실패');
      }
    } catch (error) {
      console.error('스케줄 추가 실패: ', error);
    }
  };

  const resetScheduleForm = () => {
    setScheduleFormData({
      performance: null,
      show_datetime: '',
      status: '',
    });
  };

  const handleScheduleFormCancel = () => {
    setShowScheduleCreateDialog(false);
    // setScheduleEditingPerformance(null);
    resetScheduleForm();
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'ONGOING':
        return 'default';
      case 'UPCOMING':
        return 'secondary';
      case 'ENDED':
        return 'outline';
      case 'CANCELLED':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('ko-KR').format(price) + '원';
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('ko-KR');
  };

  if (initialLoading) {
    return (
      <div className="space-y-4">
        <Card className="animate-pulse">
          <CardContent className="p-6">
            <div className="h-8 bg-muted rounded w-1/3 mb-4"></div>
            <div className="space-y-2">
              <div className="h-4 bg-muted rounded"></div>
              <div className="h-4 bg-muted rounded w-2/3"></div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2>Performance Management</h2>
          <p className="text-sm text-muted-foreground">
            Manage performances, schedules, and venue assignments
          </p>
        </div>
        <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Add Performance
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New Performance</DialogTitle>
            </DialogHeader>
            <PerformanceForm
              formData={formData}
              setFormData={setFormData}
              venues={venues}
              onSubmit={handleCreatePerformance}
              onCancel={handleFormCancel}
            />
          </DialogContent>
        </Dialog>
        <Dialog open={showCreateScheduleDialog} onOpenChange={setShowScheduleCreateDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Add Schedule
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New Schedule</DialogTitle>
            </DialogHeader>
            <ScheduleForm
              formData={scheduleFormData}
              setFormData={setScheduleFormData}
              performances={performances}
              onSubmit={handleCreateSchedule}
              onCancel={handleScheduleFormCancel}
            />
          </DialogContent>
        </Dialog>
      </div>

      {/* Performance Stats */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Calendar className="w-5 h-5 text-blue-500" />
              <div>
                <p className="text-sm text-muted-foreground">Total Performances</p>
                <p className="text-xl font-medium">{performances.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Users className="w-5 h-5 text-green-500" />
              <div>
                <p className="text-sm text-muted-foreground">Total Bookings</p>
                <p className="text-xl font-medium">
                  {performances
                    .reduce((sum, p) => sum + (p.total_bookings || 0), 0)
                    .toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <MapPin className="w-5 h-5 text-purple-500" />
              <div>
                <p className="text-sm text-muted-foreground">Active Venues</p>
                <p className="text-xl font-medium">{venues.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <div className="w-5 h-5 bg-yellow-500 rounded"></div>
              <div>
                <p className="text-sm text-muted-foreground">Total Revenue</p>
                <p className="text-xl font-medium">
                  {formatPrice(performances.reduce((sum, p) => sum + (p.revenue || 0), 0))}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Performance Table */}
      <Card>
        <CardHeader>
          <CardTitle>All Performances</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Title</TableHead>
                <TableHead>Venue</TableHead>
                <TableHead>Theme</TableHead>
                <TableHead>Dates</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Bookings</TableHead>
                <TableHead>Revenue</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {performances.map((performance) => (
                <TableRow key={performance.performance_id}>
                  <TableCell>
                    <div className="flex items-center gap-3">
                      <img
                        src={performance.poster_url}
                        alt={performance.title}
                        className="w-10 h-10 object-cover rounded"
                      />
                      <div>
                        <p className="font-medium">{performance.title}</p>
                        <p className="text-sm text-muted-foreground">
                          {performance.running_time}min
                        </p>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>{performance.venue_name}</TableCell>
                  <TableCell>
                    <Badge variant="outline">{performance.theme}</Badge>
                  </TableCell>
                  <TableCell>
                    <div className="text-sm">
                      <div>{formatDate(performance.start_date)}</div>
                      <div className="text-muted-foreground">
                        to {formatDate(performance.end_date)}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={getStatusColor(performance.status)}>{performance.status}</Badge>
                  </TableCell>
                  <TableCell>{(performance.total_bookings || 0).toLocaleString()}</TableCell>
                  <TableCell>{formatPrice(performance.revenue || 0)}</TableCell>
                  <TableCell>
                    <div className="flex gap-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleEditPerformance(performance)}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeletePerformance(performance.performance_id)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Edit Dialog */}
      <Dialog
        open={!!editingPerformance}
        onOpenChange={(open: any) => {
          if (!open) {
            setEditingPerformance(null);
            resetForm();
          }
        }}
      >
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit Performance</DialogTitle>
          </DialogHeader>
          <PerformanceForm
            formData={formData}
            setFormData={setFormData}
            venues={venues}
            onSubmit={handleUpdatePerformance}
            onCancel={handleFormCancel}
            isEdit
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardContent, CardTitle, Badge } from '@packages/design-system';

import {
  LineChart,
  Line,
  AreaChart,
  Area,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from 'recharts';
import { Users, Activity, TrendingUp, Server, Clock, AlertTriangle } from 'lucide-react';

interface SystemMetrics {
  timestamp: string;
  active_users: number;
  queue_length: number;
  cpu_usage: number;
  memory_usage: number;
  request_count: number;
  avg_response_time: number;
}

interface SystemOverviewProps {
  permissions: {
    canViewMetrics: boolean;
    canControlTraffic: boolean;
  };
}

export function SystemOverview({ permissions }: SystemOverviewProps) {
  const [metrics, setMetrics] = useState<SystemMetrics[]>([]);
  const [currentMetrics, setCurrentMetrics] = useState<SystemMetrics | null>(null);
  const [alerts, setAlerts] = useState<
    { id: number; type: string; message: string; severity: 'low' | 'medium' | 'high' }[]
  >([]);

  useEffect(() => {
    // Generate mock metrics data for the last 24 hours
    const generateMetrics = () => {
      const data: SystemMetrics[] = [];
      const now = new Date();

      for (let i = 23; i >= 0; i--) {
        const timestamp = new Date(now.getTime() - i * 60 * 60 * 1000);
        const hour = timestamp.getHours();

        // Simulate traffic patterns (higher during evening hours)
        const trafficMultiplier =
          hour >= 18 && hour <= 23 ? 2.5 : hour >= 9 && hour <= 17 ? 1.5 : 0.5;

        data.push({
          timestamp: timestamp.toISOString(),
          active_users: Math.floor(Math.random() * 1000 * trafficMultiplier) + 100,
          queue_length: Math.floor(Math.random() * 50 * trafficMultiplier),
          cpu_usage: Math.random() * 80 + 10,
          memory_usage: Math.random() * 70 + 20,
          request_count: Math.floor(Math.random() * 5000 * trafficMultiplier) + 500,
          avg_response_time: Math.random() * 200 + 50,
        });
      }

      return data;
    };

    const metricsData = generateMetrics();
    setMetrics(metricsData);
    setCurrentMetrics(metricsData[metricsData.length - 1]);

    // Generate mock alerts
    setAlerts([
      {
        id: 1,
        type: 'Performance',
        message: 'High CPU usage detected on server-01 (85%)',
        severity: 'high',
      },
      {
        id: 2,
        type: 'Traffic',
        message: 'Queue length exceeded threshold (45 users)',
        severity: 'medium',
      },
      {
        id: 3,
        type: 'System',
        message: 'Memory usage approaching limit (78%)',
        severity: 'medium',
      },
    ]);

    // Simulate real-time updates
    const interval = setInterval(() => {
      const newMetric: SystemMetrics = {
        timestamp: new Date().toISOString(),
        active_users: Math.floor(Math.random() * 2000) + 100,
        queue_length: Math.floor(Math.random() * 100),
        cpu_usage: Math.random() * 90 + 5,
        memory_usage: Math.random() * 80 + 15,
        request_count: Math.floor(Math.random() * 10000) + 500,
        avg_response_time: Math.random() * 300 + 30,
      };

      setCurrentMetrics(newMetric);
      setMetrics((prev) => [...prev.slice(1), newMetric]);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getAlertColor = (severity: string) => {
    switch (severity) {
      case 'high':
        return 'destructive';
      case 'medium':
        return 'secondary';
      case 'low':
        return 'outline';
      default:
        return 'outline';
    }
  };

  const getStatusColor = (value: number, thresholds: { warning: number; critical: number }) => {
    if (value >= thresholds.critical) return 'text-red-500';
    if (value >= thresholds.warning) return 'text-yellow-500';
    return 'text-green-500';
  };

  if (!permissions.canViewMetrics) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <p className="text-muted-foreground">
            Access denied. You don't have permission to view system metrics.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Current System Status */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Active Users</p>
                <p className="text-2xl font-medium">
                  {currentMetrics?.active_users.toLocaleString()}
                </p>
              </div>
              <Users className="w-8 h-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Queue Length</p>
                <p
                  className={`text-2xl font-medium ${getStatusColor(currentMetrics?.queue_length || 0, { warning: 30, critical: 50 })}`}
                >
                  {currentMetrics?.queue_length}
                </p>
              </div>
              <Activity className="w-8 h-8 text-orange-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">CPU Usage</p>
                <p
                  className={`text-2xl font-medium ${getStatusColor(currentMetrics?.cpu_usage || 0, { warning: 70, critical: 85 })}`}
                >
                  {currentMetrics?.cpu_usage.toFixed(1)}%
                </p>
              </div>
              <Server className="w-8 h-8 text-green-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Avg Response</p>
                <p
                  className={`text-2xl font-medium ${getStatusColor(currentMetrics?.avg_response_time || 0, { warning: 200, critical: 500 })}`}
                >
                  {currentMetrics?.avg_response_time.toFixed(0)}ms
                </p>
              </div>
              <Clock className="w-8 h-8 text-purple-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* System Alerts */}
      {alerts.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <AlertTriangle className="w-5 h-5" />
              System Alerts
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {alerts.map((alert) => (
                <div
                  key={alert.id}
                  className="flex items-center justify-between p-3 bg-muted rounded-lg"
                >
                  <div>
                    <p className="font-medium">{alert.message}</p>
                    <p className="text-sm text-muted-foreground">{alert.type}</p>
                  </div>
                  <Badge variant={getAlertColor(alert.severity)}>
                    {alert.severity.toUpperCase()}
                  </Badge>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Charts */}
      <div className="grid gap-6 lg:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>Active Users (24h)</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <AreaChart data={metrics}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="timestamp" tickFormatter={formatTime} tick={{ fontSize: 12 }} />
                <YAxis />
                <Tooltip
                  labelFormatter={formatTime}
                  formatter={(value) => [value, 'Active Users']}
                />
                <Area
                  type="monotone"
                  dataKey="active_users"
                  stroke="#8884d8"
                  fill="#8884d8"
                  fillOpacity={0.3}
                />
              </AreaChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>System Performance</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={metrics}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="timestamp" tickFormatter={formatTime} tick={{ fontSize: 12 }} />
                <YAxis />
                <Tooltip
                  labelFormatter={formatTime}
                  formatter={(value, name) => [
                    name === 'avg_response_time' ? `${value}ms` : `${value}%`,
                    name === 'cpu_usage'
                      ? 'CPU'
                      : name === 'memory_usage'
                        ? 'Memory'
                        : 'Response Time',
                  ]}
                />
                <Line type="monotone" dataKey="cpu_usage" stroke="#82ca9d" strokeWidth={2} />
                <Line type="monotone" dataKey="memory_usage" stroke="#ffc658" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Request Count</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={metrics}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="timestamp" tickFormatter={formatTime} tick={{ fontSize: 12 }} />
                <YAxis />
                <Tooltip labelFormatter={formatTime} formatter={(value) => [value, 'Requests']} />
                <Bar dataKey="request_count" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Queue Length</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={metrics}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="timestamp" tickFormatter={formatTime} tick={{ fontSize: 12 }} />
                <YAxis />
                <Tooltip
                  labelFormatter={formatTime}
                  formatter={(value) => [value, 'Queue Length']}
                />
                <Line type="monotone" dataKey="queue_length" stroke="#ff7300" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  CardTitle,
  Button,
  Badge,
  Input,
  Slider,
  Alert,
  AlertDescription,
} from '@packages/design-system';

import {
  Server,
  Plus,
  Minus,
  Activity,
  Zap,
  Shield,
  AlertTriangle,
  CheckCircle,
  Clock,
} from 'lucide-react';

interface ServerInstance {
  id: string;
  name: string;
  status: 'running' | 'starting' | 'stopping' | 'stopped';
  cpu: number;
  memory: number;
  requests_per_minute: number;
  uptime: string;
}

interface TrafficControlProps {
  permissions: {
    canControlTraffic: boolean;
    canScaleInfrastructure: boolean;
  };
}

export function TrafficControl({ permissions }: TrafficControlProps) {
  const [servers, setServers] = useState<ServerInstance[]>([]);
  const [autoScaling, setAutoScaling] = useState(true);
  const [loadBalancing, setLoadBalancing] = useState(true);
  const [rateLimiting, setRateLimiting] = useState(true);
  const [rateLimit, setRateLimit] = useState([100]);
  const [scaling, setScaling] = useState(false);
  const [targetServerCount, setTargetServerCount] = useState('');
  const [notifications, setNotifications] = useState<
    {
      id: number;
      message: string;
      type: 'success' | 'warning' | 'error';
      timestamp: Date;
    }[]
  >([]);

  useEffect(() => {
    // Initialize mock servers
    setServers([
      {
        id: 'server-01',
        name: 'Web Server 01',
        status: 'running',
        cpu: 65.2,
        memory: 78.5,
        requests_per_minute: 1250,
        uptime: '15d 8h 32m',
      },
      {
        id: 'server-02',
        name: 'Web Server 02',
        status: 'running',
        cpu: 58.7,
        memory: 72.1,
        requests_per_minute: 1180,
        uptime: '15d 8h 32m',
      },
      {
        id: 'server-03',
        name: 'Web Server 03',
        status: 'running',
        cpu: 42.3,
        memory: 55.8,
        requests_per_minute: 890,
        uptime: '12d 4h 15m',
      },
    ]);

    // Add initial notification
    addNotification('System initialized successfully', 'success');
  }, []);

  const addNotification = (message: string, type: 'success' | 'warning' | 'error') => {
    const id = Date.now();
    setNotifications((prev) => [...prev, { id, message, type, timestamp: new Date() }]);

    // Auto-remove after 5 seconds
    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  };

  const handleScaleServers = async () => {
    if (!permissions.canScaleInfrastructure) {
      addNotification('Access denied: Cannot scale infrastructure', 'error');
      return;
    }

    const targetCount = parseInt(targetServerCount);

    if (isNaN(targetCount) || targetCount < 1 || targetCount > 20) {
      addNotification('Invalid server count. Please enter a number between 1 and 20', 'error');
      return;
    }

    const currentCount = servers.filter(
      (s) => s.status === 'running' || s.status === 'starting',
    ).length;

    if (targetCount === currentCount) {
      addNotification(`Already have ${currentCount} servers running`, 'warning');
      return;
    }

    setScaling(true);
    setTargetServerCount('');

    if (targetCount > currentCount) {
      // Scale up
      const serversToAdd = targetCount - currentCount;
      addNotification(`Scaling up: Adding ${serversToAdd} server(s)...`, 'warning');
      addNotification(
        'Executing: aws ec2 run-instances --count ' + serversToAdd + ' --image-id ami-xxx...',
        'warning',
      );

      for (let i = 0; i < serversToAdd; i++) {
        setTimeout(() => {
          const newServerId = `server-${String(servers.length + i + 1).padStart(2, '0')}`;
          const newServer: ServerInstance = {
            id: newServerId,
            name: `Web Server ${String(servers.length + i + 1).padStart(2, '0')}`,
            status: 'starting',
            cpu: 0,
            memory: 0,
            requests_per_minute: 0,
            uptime: '0m',
          };

          setServers((prev) => [...prev, newServer]);
          addNotification(`Server ${newServer.name} is starting up`, 'success');

          // Simulate server becoming ready
          setTimeout(
            () => {
              setServers((prev) =>
                prev.map((server) =>
                  server.id === newServerId
                    ? {
                        ...server,
                        status: 'running',
                        cpu: Math.random() * 40 + 20,
                        memory: Math.random() * 30 + 30,
                        requests_per_minute: Math.floor(Math.random() * 500 + 400),
                      }
                    : server,
                ),
              );
              addNotification(`Server ${newServer.name} is now online and ready`, 'success');

              if (i === serversToAdd - 1) {
                setScaling(false);
                addNotification(`Successfully scaled to ${targetCount} servers`, 'success');
              }
            },
            3000 + i * 1000,
          );
        }, i * 2000);
      }
    } else {
      // Scale down
      const serversToRemove = currentCount - targetCount;
      addNotification(`Scaling down: Removing ${serversToRemove} server(s)...`, 'warning');

      const runningServers = servers.filter((s) => s.status === 'running');
      const serversToStop = runningServers.slice(-serversToRemove);

      serversToStop.forEach((server, i) => {
        setTimeout(() => {
          addNotification(`Gracefully shutting down ${server.name}`, 'warning');

          setServers((prev) =>
            prev.map((s) => (s.id === server.id ? { ...s, status: 'stopping' } : s)),
          );

          setTimeout(() => {
            setServers((prev) => prev.filter((s) => s.id !== server.id));
            addNotification(`Server ${server.name} has been terminated`, 'success');

            if (i === serversToRemove - 1) {
              setScaling(false);
              addNotification(`Successfully scaled down to ${targetCount} servers`, 'success');
            }
          }, 3000);
        }, i * 2000);
      });
    }
  };

  const toggleAutoScaling = () => {
    if (!permissions.canControlTraffic) {
      addNotification('Access denied: Cannot modify traffic control settings', 'error');
      return;
    }

    setAutoScaling(!autoScaling);
    addNotification(
      `Auto-scaling ${!autoScaling ? 'enabled' : 'disabled'}`,
      !autoScaling ? 'success' : 'warning',
    );
  };

  const toggleLoadBalancing = () => {
    if (!permissions.canControlTraffic) {
      addNotification('Access denied: Cannot modify load balancing', 'error');
      return;
    }

    setLoadBalancing(!loadBalancing);
    addNotification(
      `Load balancing ${!loadBalancing ? 'enabled' : 'disabled'}`,
      !loadBalancing ? 'success' : 'warning',
    );
  };

  const updateRateLimit = (value: number[]) => {
    if (!permissions.canControlTraffic) {
      addNotification('Access denied: Cannot modify rate limiting', 'error');
      return;
    }

    setRateLimit(value);
    addNotification(`Rate limit updated to ${value[0]} requests/minute per IP`, 'success');
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'running':
        return 'default';
      case 'starting':
        return 'secondary';
      case 'stopping':
        return 'secondary';
      case 'stopped':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'success':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="w-4 h-4 text-yellow-500" />;
      case 'error':
        return <AlertTriangle className="w-4 h-4 text-red-500" />;
      default:
        return <Clock className="w-4 h-4" />;
    }
  };

  if (!permissions.canControlTraffic && !permissions.canScaleInfrastructure) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <p className="text-muted-foreground">
            Access denied. You don't have permission to control traffic or infrastructure.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Notifications */}
      {notifications.length > 0 && (
        <div className="space-y-2">
          {notifications.map((notification) => (
            <Alert key={notification.id} className="flex items-center gap-2">
              {getNotificationIcon(notification.type)}
              <AlertDescription className="flex-1">{notification.message}</AlertDescription>
              <span className="text-xs text-muted-foreground">
                {notification.timestamp.toLocaleTimeString()}
              </span>
            </Alert>
          ))}
        </div>
      )}

      {/* Server Instances */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Server className="w-5 h-5" />
              Server Instances
            </CardTitle>
            <div className="flex flex-col gap-2">
              <div className="flex gap-2 items-center">
                <div className="text-sm text-muted-foreground min-w-fit">
                  Current:{' '}
                  {servers.filter((s) => s.status === 'running' || s.status === 'starting').length}{' '}
                  servers
                </div>
                <Input
                  type="number"
                  placeholder="Target"
                  value={targetServerCount}
                  onChange={(e) => setTargetServerCount(e.target.value)}
                  min="1"
                  max="20"
                  className="w-20"
                  disabled={scaling || !permissions.canScaleInfrastructure}
                />
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleScaleServers}
                  disabled={scaling || !permissions.canScaleInfrastructure || !targetServerCount}
                >
                  <Server className="w-4 h-4 mr-1" />
                  {scaling ? 'Scaling...' : 'Send'}
                </Button>
              </div>
              <p className="text-xs text-muted-foreground">
                Enter desired server count (1-20) and click Send
              </p>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {servers.map((server) => (
              <Card key={server.id} className="p-4">
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <h4 className="font-medium">{server.name}</h4>
                    <Badge variant={getStatusColor(server.status)}>{server.status}</Badge>
                  </div>

                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span>CPU:</span>
                      <span
                        className={
                          server.cpu > 80
                            ? 'text-red-500'
                            : server.cpu > 60
                              ? 'text-yellow-500'
                              : 'text-green-500'
                        }
                      >
                        {server.cpu.toFixed(1)}%
                      </span>
                    </div>

                    <div className="flex justify-between">
                      <span>Memory:</span>
                      <span
                        className={
                          server.memory > 85
                            ? 'text-red-500'
                            : server.memory > 70
                              ? 'text-yellow-500'
                              : 'text-green-500'
                        }
                      >
                        {server.memory.toFixed(1)}%
                      </span>
                    </div>

                    <div className="flex justify-between">
                      <span>Requests/min:</span>
                      <span>{server.requests_per_minute.toLocaleString()}</span>
                    </div>

                    <div className="flex justify-between">
                      <span>Uptime:</span>
                      <span>{server.uptime}</span>
                    </div>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Traffic Control Settings */}
      <div className="grid gap-6 lg:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Activity className="w-5 h-5" />
              Auto Scaling
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium">Auto Scaling Status</p>
                <p className="text-sm text-muted-foreground">
                  Automatically scale servers based on traffic
                </p>
              </div>
              <Button
                variant={autoScaling ? 'default' : 'outline'}
                onClick={toggleAutoScaling}
                disabled={!permissions.canControlTraffic}
              >
                {autoScaling ? 'Enabled' : 'Disabled'}
              </Button>
            </div>

            {autoScaling && (
              <div className="space-y-2 p-4 bg-muted rounded-lg">
                <p className="text-sm font-medium">Thresholds:</p>
                <ul className="text-xs space-y-1 text-muted-foreground">
                  <li>• Scale up when CPU &gt; 70% for 5 minutes</li>
                  <li>• Scale down when CPU &lt; 30% for 10 minutes</li>
                  <li>• Maximum instances: 10</li>
                  <li>• Minimum instances: 1</li>
                </ul>
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Zap className="w-5 h-5" />
              Load Balancing
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium">Load Balancer Status</p>
                <p className="text-sm text-muted-foreground">Distribute traffic across servers</p>
              </div>
              <Button
                variant={loadBalancing ? 'default' : 'outline'}
                onClick={toggleLoadBalancing}
                disabled={!permissions.canControlTraffic}
              >
                {loadBalancing ? 'Active' : 'Inactive'}
              </Button>
            </div>

            {loadBalancing && (
              <div className="space-y-2 p-4 bg-muted rounded-lg">
                <p className="text-sm font-medium">Configuration:</p>
                <ul className="text-xs space-y-1 text-muted-foreground">
                  <li>• Algorithm: Round Robin</li>
                  <li>• Health checks: Every 30 seconds</li>
                  <li>• Sticky sessions: Disabled</li>
                  <li>• SSL termination: Enabled</li>
                </ul>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Rate Limiting */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Shield className="w-5 h-5" />
            Rate Limiting
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="font-medium">Rate Limiting</p>
              <p className="text-sm text-muted-foreground">Control request rate per IP address</p>
            </div>
            <Button
              variant={rateLimiting ? 'default' : 'outline'}
              onClick={() => setRateLimiting(!rateLimiting)}
              disabled={!permissions.canControlTraffic}
            >
              {rateLimiting ? 'Enabled' : 'Disabled'}
            </Button>
          </div>

          {rateLimiting && (
            <div className="space-y-4">
              <div>
                <p className="text-sm font-medium mb-2">
                  Requests per minute per IP: {rateLimit[0]}
                </p>
                <Slider
                  value={rateLimit}
                  onValueChange={updateRateLimit}
                  max={1000}
                  min={10}
                  step={10}
                  className="w-full"
                  disabled={!permissions.canControlTraffic}
                />
              </div>

              <div className="p-4 bg-muted rounded-lg">
                <p className="text-sm font-medium">Current Status:</p>
                <ul className="text-xs space-y-1 text-muted-foreground mt-1">
                  <li>• Blocked IPs today: 12</li>
                  <li>• Rate limited requests: 1,847</li>
                  <li>• Average requests/min: 8,450</li>
                </ul>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  CardTitle,
  Button,
  Input,
  Label,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Badge,
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@packages/design-system';

import { Plus, Edit, Trash2, Search, Shield, Users, UserCheck, AlertTriangle } from 'lucide-react';
import { userService } from '@/entities/user';
import type { User } from '@/entities/user';

const UserForm = ({
  isEdit = false,
  formData,
  setFormData,
  handleCreateUser,
  handleUpdateUser,
  setShowCreateDialog,
  setEditingUser,
  resetForm,
}: {
  isEdit?: boolean;
  formData: any;
  setFormData: any;
  handleCreateUser: () => void;
  handleUpdateUser: () => void;
  setShowCreateDialog: (show: boolean) => void;
  setEditingUser: (user: any) => void;
  resetForm: () => void;
}) => (
  <div className="space-y-4">
    <div className="grid grid-cols-2 gap-4">
      <div>
        <Label htmlFor="email">Email</Label>
        <Input
          id="email"
          type="email"
          value={formData.email}
          onChange={(e) => setFormData((prev: any) => ({ ...prev, email: e.target.value }))}
          placeholder="user@example.com"
        />
      </div>

      <div>
        <Label htmlFor="username">Username</Label>
        <Input
          id="username"
          value={formData.username}
          onChange={(e) => setFormData((prev: any) => ({ ...prev, username: e.target.value }))}
          placeholder="username"
        />
      </div>
    </div>

    <div>
      <Label htmlFor="name">Full Name</Label>
      <Input
        id="name"
        value={formData.name}
        onChange={(e) => setFormData((prev: any) => ({ ...prev, name: e.target.value }))}
        placeholder="John Doe"
      />
    </div>

    <div className="grid grid-cols-2 gap-4">
      <div>
        <Label htmlFor="phone">Phone</Label>
        <Input
          id="phone"
          value={formData.phone}
          onChange={(e) => setFormData((prev: any) => ({ ...prev, phone: e.target.value }))}
          placeholder="010-1234-5678"
        />
      </div>

      <div>
        <Label htmlFor="role">Role</Label>
        <Select
          value={formData.role}
          onValueChange={(value) => setFormData((prev: any) => ({ ...prev, role: value }))}
        >
          <SelectTrigger>
            <SelectValue placeholder="Select role" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="USER">User</SelectItem>
            <SelectItem value="DEV">Developer</SelectItem>
            <SelectItem value="DEVOPS">DevOps</SelectItem>
            <SelectItem value="ADMIN">Admin</SelectItem>
          </SelectContent>
        </Select>
      </div>
    </div>

    <div>
      <Label htmlFor="password">
        {isEdit ? 'New Password (leave blank to keep current)' : 'Password'}
      </Label>
      <Input
        id="password"
        type="password"
        value={formData.password}
        onChange={(e) => setFormData((prev: any) => ({ ...prev, password: e.target.value }))}
        placeholder={isEdit ? 'Leave blank to keep current password' : 'Enter password'}
      />
    </div>

    <div className="flex justify-end gap-2 pt-4">
      <Button
        variant="outline"
        onClick={() => {
          setShowCreateDialog(false);
          setEditingUser(null);
          resetForm();
        }}
      >
        Cancel
      </Button>
      <Button onClick={isEdit ? handleUpdateUser : handleCreateUser}>
        {isEdit ? 'Update' : 'Create'} User
      </Button>
    </div>
  </div>
);

export function UserManagement() {
  const [users, setUsers] = useState<User[]>([]);
  const [filteredUsers, setFilteredUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState<string>('');
  const [statusFilter, setStatusFilter] = useState<string>('');
  const [editingUser, setEditingUser] = useState<User | null>(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    name: '',
    phone: '',
    role: 'USER' as const,
    password: '',
  });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        setLoading(true);
        const userData = await userService.getUsers();

        setUsers(userData);
      } catch (error) {
        console.error('사용자 데이터를 가져오는데 실패했습니다:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  const searchUsers = async (searchParams?: {
    username?: string;
    role?: string;
    status?: string;
  }) => {
    setLoading(true);

    try {
      const userData = await userService.searchUsers({
        username: searchParams?.username || '',
        role: searchParams?.role === 'all' ? '' : searchParams?.role || '',
        status: searchParams?.status === 'all' ? '' : searchParams?.status || '',
      });

      setFilteredUsers(userData);
      console.log(userData);
    } catch (error) {
      console.error('Failed to search users: ', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const delayDebounceTimer = setTimeout(() => {
      searchUsers({
        username: searchTerm,
        role: roleFilter,
        status: statusFilter,
      });
    }, 500);

    return () => clearTimeout(delayDebounceTimer);
  }, [searchTerm]);

  useEffect(() => {
    searchUsers({
      username: searchTerm,
      role: roleFilter,
      status: statusFilter,
    });
  }, [users, roleFilter, statusFilter]);

  const handleCreateUser = async () => {
    try {
      setLoading(true);

      const newUser = await userService.createUser({
        email: formData.email,
        username: formData.username,
        name: formData.name,
        phone: formData.phone,
        role: formData.role,
        password: formData.password,
      });

      if (newUser !== undefined) {
        setUsers((prev) => [...prev, newUser]);
        setShowCreateDialog(false);
        resetForm();
        console.log('사용자 생성 성공');
      } else {
        throw new Error('사용자 생성 실패');
      }
    } catch (error) {
      console.error('사용자 생성 실패: ', error);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateUser = () => {
    if (!editingUser) return;

    setUsers((prev) =>
      prev.map((user) => (user.user_id === editingUser.user_id ? { ...user, ...formData } : user)),
    );
    setEditingUser(null);
    resetForm();
  };

  const handleDeleteUser = async (id: number) => {
    if (!window.confirm('Are you sure you want to delete this user?')) {
      return;
    }

    try {
      setLoading(true);

      const success = await userService.deleteUser(id);

      if (success) {
        setUsers((prev) => prev.filter((user) => user.user_id !== id));
        console.log('사용자가 삭제 성공');
      } else {
        throw new Error('사용자 삭제 실패');
      }
    } catch (error) {
      console.error('사용자 삭제 실패: ', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEditUser = (user: User) => {
    setEditingUser(user);
    setFormData({
      email: user.email,
      username: user.username,
      name: user.name,
      phone: user.phone || '',
      role: user.role,
      password: '',
    });
  };

  const handleStatusChange = (userId: number, newStatus: 'ACTIVE' | 'INACTIVE' | 'SUSPENDED') => {
    setUsers((prev) =>
      prev.map((user) => (user.user_id === userId ? { ...user, status: newStatus } : user)),
    );
  };

  const resetForm = () => {
    setFormData({
      email: '',
      username: '',
      name: '',
      phone: '',
      role: 'USER',
      password: '',
    });
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'ADMIN':
        return 'destructive';
      case 'DEVOPS':
        return 'default';
      case 'DEV':
        return 'secondary';
      case 'USER':
        return 'outline';
      default:
        return 'outline';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'ACTIVE':
        return 'default';
      case 'INACTIVE':
        return 'secondary';
      case 'SUSPENDED':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('ko-KR').format(price) + '원';
  };
  if (loading) {
    return (
      <div className="space-y-4">
        <Card className="animate-pulse">
          <CardContent className="p-6">
            <div className="h-8 bg-muted rounded w-1/3 mb-4"></div>
            <div className="space-y-2">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-4 bg-muted rounded"></div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2>User Management</h2>
          <p className="text-sm text-muted-foreground">
            Manage user accounts, roles, and permissions
          </p>
        </div>
        <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Add User
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New User</DialogTitle>
            </DialogHeader>
            <UserForm
              formData={formData}
              setFormData={setFormData}
              handleCreateUser={handleCreateUser}
              handleUpdateUser={handleUpdateUser}
              setShowCreateDialog={setShowCreateDialog}
              setEditingUser={setEditingUser}
              resetForm={resetForm}
            />
          </DialogContent>
        </Dialog>
      </div>

      {/* User Stats */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Users className="w-5 h-5 text-blue-500" />
              <div>
                <p className="text-sm text-muted-foreground">Total Users</p>
                <p className="text-xl font-medium">{users.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <UserCheck className="w-5 h-5 text-green-500" />
              <div>
                <p className="text-sm text-muted-foreground">Active Users</p>
                <p className="text-xl font-medium">
                  {users.filter((u) => u.status === 'ACTIVE').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Shield className="w-5 h-5 text-purple-500" />
              <div>
                <p className="text-sm text-muted-foreground">Admin/Staff</p>
                <p className="text-xl font-medium">
                  {users.filter((u) => ['ADMIN', 'DEVOPS', 'DEV'].includes(u.role)).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <AlertTriangle className="w-5 h-5 text-red-500" />
              <div>
                <p className="text-sm text-muted-foreground">Suspended</p>
                <p className="text-xl font-medium">
                  {users.filter((u) => u.status === 'SUSPENDED').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-wrap gap-4">
            <div className="flex items-center gap-2">
              <Search className="w-4 h-4" />
              <Input
                placeholder="Search users..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-64"
              />
            </div>

            <Select value={roleFilter} onValueChange={setRoleFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Role" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Roles</SelectItem>
                <SelectItem value="USER">Users</SelectItem>
                <SelectItem value="DEV">Developers</SelectItem>
                <SelectItem value="DEVOPS">DevOps</SelectItem>
                <SelectItem value="ADMIN">Admins</SelectItem>
              </SelectContent>
            </Select>

            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="ACTIVE">Active</SelectItem>
                <SelectItem value="INACTIVE">Inactive</SelectItem>
                <SelectItem value="SUSPENDED">Suspended</SelectItem>
              </SelectContent>
            </Select>

            <div className="text-sm text-muted-foreground flex items-center">
              Showing {filteredUsers.length} of {users.length} users
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Users Table */}
      <Card>
        <CardHeader>
          <CardTitle>All Users</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>User</TableHead>
                <TableHead>Role</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Last Login</TableHead>
                <TableHead>Bookings</TableHead>
                <TableHead>Total Spent</TableHead>
                <TableHead>Joined</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredUsers.map((user) => (
                <TableRow key={user.user_id}>
                  <TableCell>
                    <div>
                      <div className="font-medium">{user.name}</div>
                      <div className="text-sm text-muted-foreground">{user.email}</div>
                      <div className="text-xs text-muted-foreground">{user.username}</div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={getRoleColor(user.role)}>{user.role}</Badge>
                  </TableCell>
                  <TableCell>
                    <Badge variant={getStatusColor(user.status)}>{user.status}</Badge>
                  </TableCell>
                  <TableCell>{user.last_login ? formatDate(user.last_login) : 'Never'}</TableCell>
                  <TableCell>{user.total_bookings}</TableCell>
                  <TableCell>{formatPrice(user.total_spent)}</TableCell>
                  <TableCell>{formatDate(user.created_at)}</TableCell>
                  <TableCell>
                    <div className="flex gap-1">
                      <Button variant="ghost" size="sm" onClick={() => handleEditUser(user)}>
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeleteUser(user.userId)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Edit Dialog */}
      <Dialog
        open={!!editingUser}
        onOpenChange={(open) => {
          if (!open) {
            setEditingUser(null);
            resetForm();
          }
        }}
      >
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit User</DialogTitle>
          </DialogHeader>
          <UserForm
            isEdit
            formData={formData}
            setFormData={setFormData}
            handleCreateUser={handleCreateUser}
            handleUpdateUser={handleUpdateUser}
            setShowCreateDialog={setShowCreateDialog}
            setEditingUser={setEditingUser}
            resetForm={resetForm}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

