/**
 * Client App Configuration
 * FSD v2.1: App Layer - Config
 */

import { commonConfig } from '@shared';

export const appConfig = {
  // App-specific URL
  appUrl: import.meta.env.VITE_APP_URL || commonConfig.urls.client,

  // Common URLs from shared config
  accountsUrl: commonConfig.urls.accounts,
  adminUrl: commonConfig.urls.admin,

  // API from shared config
  apiBase: commonConfig.apiBase,

  // Cookie from shared config
  cookieDomain: commonConfig.cookieDomain,

  // Routes
  routes: {
    home: commonConfig.routes.home,
    accountsSelect: commonConfig.routes.accountsSelect,
  },
} as const;

import { BrowserRouter } from 'react-router-dom';
import { ClientAuthProvider } from './providers/ClientAuthProvider';
import { AppRoutes } from './routes';

export function App() {
  return (
    <BrowserRouter>
      <ClientAuthProvider>
        <AppRoutes />
      </ClientAuthProvider>
    </BrowserRouter>
  );
}

export default App;

/**
 * Client app Layout component
 * Uses ClientHeader widget with integrated navigation
 */

import { Outlet } from 'react-router-dom';
import { Container } from '@packages/design-system';
import { ClientHeader } from '../../widgets/client-header';
import { appConfig } from '../config/config';

export function Layout() {
  return (
    <div className="flex min-h-screen flex-col bg-background">
      <ClientHeader />

      <main className="flex-1 py-8">
        <Container>
          <Outlet />
        </Container>
      </main>

      <footer className="border-t bg-muted/40 py-6">
        <Container className="text-center text-sm text-muted-foreground">
          <p>티켓 예매 시스템</p>
          <p className="mt-1 text-xs">{appConfig.appUrl.replace('https://', '')}</p>
        </Container>
      </footer>
    </div>
  );
}

/**
 * Client app AuthProvider
 * Extends shared useAuthState with client-specific behavior
 */

import { createContext, useContext, useEffect, type ReactNode } from 'react';
import { useAuthState, commonConfig, type AuthContextValue } from '@shared';

interface ClientAuthContextValue extends AuthContextValue {
  login: () => void;
}

const ClientAuthContext = createContext<ClientAuthContextValue | undefined>(undefined);

export function ClientAuthProvider({ children }: { children: ReactNode }) {
  // Use shared auth state with client-specific behavior
  const { user, loading, loadUser, logout, refetchUser } = useAuthState({
    logoutUri: window.location.origin,
    onUnauthenticated: () => {
      // Client: don't auto-redirect, allow anonymous browsing
      console.log('[Client] Not authenticated - anonymous mode');
    },
    onAuthError: () => {
      // Client: log error but don't redirect
      console.warn('[Client] Auth error - staying on page');
    },
  });

  useEffect(() => {
    loadUser();
  }, []);

  const login = () => {
    // Redirect to accounts login with current page as return URL
    const returnUrl = encodeURIComponent(window.location.href);
    const accountsLoginUrl = `${commonConfig.urls.accounts}${commonConfig.routes.accountsLogin}`;
    window.location.href = `${accountsLoginUrl}?returnUrl=${returnUrl}`;
  };

  return (
    <ClientAuthContext.Provider value={{ user, loading, logout, refetchUser, login }}>
      {children}
    </ClientAuthContext.Provider>
  );
}

export function useClientAuth(): ClientAuthContextValue {
  const context = useContext(ClientAuthContext);
  if (!context) {
    throw new Error('useClientAuth must be used within ClientAuthProvider');
  }
  return context;
}

/**
 * Web App Routes
 * FSD v2.1: app/routes/index.tsx
