import { apiClient } from '@shared/api';
import { API_CONFIG } from '@shared/config/api';
import type {
  AdminPerformanceResponse,
  Performance,
  PerformanceRequest,
  ScheduleStatus,
} from '@packages/shared';

interface PresignedUrlResponse {
  presignedUrl: string;
  imageKey: string;
}

const transformAdminPerformance = (response: AdminPerformanceResponse): Performance => ({
  performance_id: response.performanceResponse.performanceId,
  title: response.performanceResponse.title,
  venue: response.performanceResponse.venue,
  venue_name: response.performanceResponse.venue,
  theme: response.performanceResponse.theme,
  description: response.performanceResponse.description || 'default text',
  poster_url: response.performanceResponse.posterUrl,
  price: response.performanceResponse.price,
  base_price: response.performanceResponse.price,
  status: response.performanceResponse.status,
  start_date: response.performanceResponse.startDate,
  end_date: response.performanceResponse.endDate,
  running_time: response.performanceResponse.runningTime,
  venue_address: response.performanceResponse.venueAddress,
  venue_id: response.performanceResponse.venueId,
  schedules: response.performanceResponse.schedules.map((schedule) => ({
    schedule_id: schedule.scheduleId,
    show_datetime: schedule.showDatetime,
    available_seats: schedule.availableSeats,
    total_seats: schedule.totalSeats,
    status: schedule.status as ScheduleStatus,
  })),
  total_bookings: response.totalBookings,
  revenue: response.revenue,
});

const uploadImage = async (image: File | null): Promise<string> => {
  if (!image) {
    return '';
  }

  const presignedResponse = await apiClient.post<PresignedUrlResponse>(
    `${API_CONFIG.ENDPOINTS.ADMIN_PERFORMANCES}/upload-url`,
    {
      imageName: image.name,
      imageType: image.type,
    },
  );

  const uploadResult = await fetch(presignedResponse.presignedUrl, {
    method: 'PUT',
    headers: {
      'Content-Type': image.type,
    },
    body: image,
  });

  if (!uploadResult.ok) {
    throw new Error('파일 업로드 실패');
  }

  return presignedResponse.imageKey;
};

export const adminPerformanceService = {
  async getPerformances(): Promise<Performance[]> {
    try {
      const response = await apiClient.get<AdminPerformanceResponse[]>(
        API_CONFIG.ENDPOINTS.ADMIN_PERFORMANCES,
      );

      if (!Array.isArray(response)) {
        return [];
      }

      return response.map(transformAdminPerformance);
    } catch (error) {
      console.error('Failed to fetch admin performances:', error);
      return [];
    }
  },

  async createPerformance(
    payload: PerformanceRequest,
    image: File | null,
  ): Promise<Performance | undefined> {
    try {
      const posterKey = await uploadImage(image);
      const response = await apiClient.post<AdminPerformanceResponse>(
        API_CONFIG.ENDPOINTS.ADMIN_PERFORMANCES,
        {
          ...payload,
          posterUrl: posterKey,
        },
      );
      return transformAdminPerformance(response);
    } catch (error) {
      console.error('Failed to create performance:', error);
      return undefined;
    }
  },

  async updatePerformance(
    performanceId: number,
    payload: PerformanceRequest,
    image: File | null,
  ): Promise<Performance | undefined> {
    try {
      const posterKey = await uploadImage(image);
      const response = await apiClient.put<AdminPerformanceResponse>(
        `${API_CONFIG.ENDPOINTS.ADMIN_PERFORMANCES}/${performanceId}`,
        {
          ...payload,
          posterUrl: posterKey,
        },
      );
      return transformAdminPerformance(response);
    } catch (error) {
      console.error('Failed to update performance:', error);
      return undefined;
    }
  },

  async deletePerformance(performanceId: number): Promise<boolean> {
    try {
      await apiClient.delete(`${API_CONFIG.ENDPOINTS.ADMIN_PERFORMANCES}/${performanceId}`);
      return true;
    } catch (error) {
      console.error('Failed to delete performance:', error);
      return false;
    }
  },
};

export type AdminPerformanceService = typeof adminPerformanceService;

import { apiClient } from '@shared/api';
import { API_CONFIG } from '@shared/config/api';
import type {
  Performance,
  PerformanceResponse,
  ScheduleResponse,
  ScheduleStatus,
} from '@packages/shared';

const transformPerformanceResponse = (perf: PerformanceResponse): Performance => ({
  performance_id: perf.performanceId,
  title: perf.title,
  venue: perf.venue,
  venue_name: perf.venue,
  venue_id: perf.venueId || 0,
  theme: perf.theme,
  poster_url: perf.posterUrl,
  price: perf.price,
  base_price: perf.price,
  status: perf.status,
  start_date: perf.startDate,
  end_date: perf.endDate,
  running_time: perf.runningTime,
  venue_address: perf.venueAddress,
  description: perf.description || perf.theme,
  schedules: (perf.schedules || []).map((schedule) => ({
    schedule_id: schedule.scheduleId,
    show_datetime: schedule.showDatetime,
    available_seats: schedule.availableSeats,
    total_seats: schedule.totalSeats || 0,
    status: schedule.status as ScheduleStatus,
  })),
});

export const performanceService = {
  async getPerformanceById(performanceId: number): Promise<Performance> {
    try {
      console.log('API - Requesting performance with ID:', performanceId);

      const endpoint = `${API_CONFIG.ENDPOINTS.PERFORMANCES}/${performanceId}`;
      const backendResponse = await apiClient.get<PerformanceResponse>(endpoint);

      console.log('API - Raw backend response:', backendResponse);
      const transformed = transformPerformanceResponse(backendResponse);
      console.log('API - Transformed performance:', transformed);
      return transformed;
    } catch (error) {
      console.error('API - Failed to fetch performance:', error);
      throw error;
    }
  },

  async getPerformanceSchedules(performanceId: number): Promise<{ schedules: ScheduleResponse[] }> {
    try {
      console.log('API - Requesting schedules for performance ID:', performanceId);

      const endpoint = `${API_CONFIG.ENDPOINTS.PERFORMANCES}/${performanceId}/schedules`;
      const response = await apiClient.get<{ schedules: ScheduleResponse[] }>(endpoint);

      console.log('API - Schedules response:', response);
      return response;
    } catch (error) {
      console.error('API - Failed to fetch schedules:', error);
      throw error;
    }
  },

  async getAllPerformances(): Promise<Performance[]> {
    try {
      const response = await apiClient.get<Performance[]>(API_CONFIG.ENDPOINTS.PERFORMANCES);
      return response;
    } catch (error) {
      console.error('API - Failed to fetch performances:', error);
      throw error;
    }
  },

  async searchPerformances(searchParams: {
    name?: string;
    venue?: string;
    status?: string;
  }): Promise<Performance[]> {
    try {
      const queryString = new URLSearchParams(
        Object.entries(searchParams).filter(([_, value]) => value && value.trim() !== ''),
      ).toString();

      const endpoint = queryString
        ? `${API_CONFIG.ENDPOINTS.PERFORMANCES}/search?${queryString}`
        : API_CONFIG.ENDPOINTS.PERFORMANCES;

      const response = await apiClient.get<PerformanceResponse[]>(endpoint);
      return response.map(transformPerformanceResponse);
    } catch (error) {
      console.error('Failed to search performances:', error);
      return [];
    }
  },
};

