            <span className="text-xl font-bold text-gray-900">₩{totalPrice.toLocaleString()}</span>
          </div>
        </div>

        <div className="grid gap-3">
          <Button
            disabled={selectedCount === 0 || loading}
            onClick={onBooking}
            className="w-full py-5 text-base font-semibold"
            variant={selectedCount === 0 ? 'secondary' : 'default'}
          >
            예매하기 {selectedCount > 0 && `(${selectedCount})`}
          </Button>
          <Button
            variant="outline"
            onClick={onReset}
            className="w-full py-5"
            disabled={selectedCount === 0}
          >
            선택 초기화
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import { App } from './app';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')!).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
);

import { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Button,
  Badge,
  Skeleton,
  Alert,
  AlertDescription,
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  Label,
  Textarea,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@packages/design-system';

import { Calendar, MapPin, Users, CreditCard, AlertTriangle } from 'lucide-react';

import { bookingService } from '@/entities/booking';
import type {
  BookingDto,
  GetBookingDetail200ResponseDto,
  CancelBookingRequestDto,
} from '@packages/shared';

interface BookingHistoryProps {
  userId?: number;
}

export default function BookingHistoryPage({ userId }: BookingHistoryProps) {
  const [bookings, setBookings] = useState<BookingDto[]>([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState<'bookedDesc' | 'bookedAsc' | 'showAsc' | 'showDesc'>(
    'bookedDesc',
  );
  const [statusFilter, setStatusFilter] = useState<'all' | 'CONFIRMED' | 'CANCELLED'>('all');
  const [cancellingBooking, setCancellingBooking] = useState<BookingDto | null>(null);
  const [bookingDetails, setBookingDetails] = useState<GetBookingDetail200ResponseDto | null>(null);
  const [cancellationReason, setCancellationReason] = useState('');
  const [selectedCancelReason, setSelectedCancelReason] = useState('');
  const [processingCancellation, setProcessingCancellation] = useState(false);
  const [viewingBooking, setViewingBooking] = useState<BookingDto | null>(null);
  const [viewingBookingDetails, setViewingBookingDetails] =
    useState<GetBookingDetail200ResponseDto | null>(null);

  // Local seat codes captured at booking time
  const [localSeatCodesMap, setLocalSeatCodesMap] = useState<Record<string, string[]>>({});
  useEffect(() => {
    try {
      const rawCodes = localStorage.getItem('bookingSeatCodes');
      if (rawCodes) {
        const parsedCodes = JSON.parse(rawCodes);
        if (parsedCodes && typeof parsedCodes === 'object') setLocalSeatCodesMap(parsedCodes);
      }
    } catch (e) {
      console.warn('Failed to load local booking seat codes', e);
      setLocalSeatCodesMap({});
    }
  }, []);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        console.log('Fetching bookings for user:', userId);
        const response = await bookingService.getBookings();
        console.log('Bookings response:', response);
        setBookings(response.bookings);
      } catch (error) {
        console.error('Failed to fetch bookings:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, [userId]);

  const handleCancelBooking = async (booking: BookingDto) => {
    setCancellingBooking(booking);
    setCancellationReason('');
    setSelectedCancelReason('');

    // 예약 상세 정보 로드 (좌석 정보가 필요한 경우만)
    try {
      const details = await bookingService.getBookingDetail(booking.bookingId);
      setBookingDetails(details);
    } catch (error) {
      console.error('Failed to fetch booking details:', error);
    }
  };

  const processCancellation = async () => {
    if (!cancellingBooking || !selectedCancelReason) return;

    setProcessingCancellation(true);

    try {
      const reason = selectedCancelReason === 'other' ? cancellationReason : selectedCancelReason;

      const cancelRequest: CancelBookingRequestDto = {
        reason: reason,
      };

      const response = await bookingService.cancelBooking(
        cancellingBooking.bookingId,
        cancelRequest,
      );

      console.log('Booking cancelled successfully:', response);

      // 예약 목록 새로고침
      const updatedBookings = await bookingService.getBookings();
      setBookings(updatedBookings.bookings);

      alert('예약이 성공적으로 취소되었습니다.');
    } catch (error) {
      console.error('Failed to cancel booking:', error);
      alert('예약 취소에 실패했습니다. 다시 시도해주세요.');
    } finally {
      setProcessingCancellation(false);
      setCancellingBooking(null);
      setBookingDetails(null);
    }
  };

  const handleViewBookingDetail = async (booking: BookingDto) => {
    setViewingBooking(booking);
    setViewingBookingDetails(null);

    // 예약 상세 정보 로드
    try {
      const details = await bookingService.getBookingDetail(booking.bookingId);
      setViewingBookingDetails(details);
    } catch (error) {
      console.error('Failed to fetch booking details:', error);
    }
  };

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const calculateRefundAmount = (booking: BookingDto) => {
    // BookingDto에는 show_datetime이 없으므로 전체 금액 반환
    // 실제로는 스케줄 정보를 가져와서 계산해야 함
    return booking.totalAmount;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'CONFIRMED':
        return 'default';
      case 'CANCELLED':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('ko-KR').format(price) + '원';
  };

  const canCancelBooking = (booking: BookingDto) => {
    // BookingDto에는 show_datetime이 없으므로 상태만으로 판단
    // 실제로는 스케줄 정보를 가져와서 시간을 확인해야 함
    return booking.status === 'CONFIRMED';
  };

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const getCancellationFee = (_booking: BookingDto) => {
    // BookingDto에는 show_datetime이 없으므로 기본값 반환
    // 실제로는 스케줄 정보를 가져와서 계산해야 함
    return 0; // No fee for now
  };

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const getRefundStatusColor = (status: string) => {
    switch (status) {
      case 'COMPLETED':
        return 'default';
      case 'PROCESSING':
        return 'secondary';
      default:
        return 'outline';
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-6 space-y-4">
        {[...Array(3)].map((_, i) => (
          <Card key={i}>
            <CardContent className="p-6 space-y-4">
              <div className="flex justify-between">
                <div className="space-y-2">
                  <Skeleton className="h-4 w-48" />
                  <Skeleton className="h-3 w-32" />
                </div>
                <Skeleton className="h-6 w-20" />
              </div>
              <Skeleton className="h-3 w-full" />
              <Skeleton className="h-8 w-24" />
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (bookings.length === 0) {
    return (
      <div className="container mx-auto px-4 py-6">
        <Card>
          <CardContent className="p-6 text-center">
            <p className="text-muted-foreground">No booking history found.</p>
            <p className="text-sm text-muted-foreground mt-2">
              Your booking history will appear here after you make your first booking.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-6 space-y-4">
      <div className="flex items-center justify-between gap-3 flex-wrap">
        <h2>Booking History</h2>
        <div className="flex items-center gap-2 ml-auto">
          <Select
            value={statusFilter}
            onValueChange={(v) => setStatusFilter(v as 'all' | 'CONFIRMED' | 'CANCELLED')}
          >
            <SelectTrigger className="w-[160px]" aria-label="Filter status">
              <SelectValue placeholder="Filter status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All statuses</SelectItem>
              <SelectItem value="CONFIRMED">Confirmed</SelectItem>
              <SelectItem value="CANCELLED">Cancelled</SelectItem>
            </SelectContent>
          </Select>
          <Select
            value={sortBy}
            onValueChange={(v) =>
              setSortBy(v as 'bookedDesc' | 'bookedAsc' | 'showAsc' | 'showDesc')
            }
          >
            <SelectTrigger className="w-[220px]">
              <SelectValue placeholder="Sort by" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="bookedDesc">Booked • Newest first</SelectItem>
              <SelectItem value="bookedAsc">Booked • Oldest first</SelectItem>
              <SelectItem value="showAsc">Show • Earliest first</SelectItem>
              <SelectItem value="showDesc">Show • Latest first</SelectItem>
            </SelectContent>
          </Select>
          <Badge variant="outline">{bookings.length} bookings</Badge>
        </div>
      </div>

      {bookings
        .filter((b) => (statusFilter === 'all' ? true : b.status === statusFilter))
        .slice()
        .sort((a, b) => {
          const aBooked = new Date(a.bookedAt || '').getTime();
          const bBooked = new Date(b.bookedAt || '').getTime();
          const aShow = a.showDate ? new Date(a.showDate).getTime() : NaN;
          const bShow = b.showDate ? new Date(b.showDate).getTime() : NaN;

          switch (sortBy) {
            case 'bookedAsc':
              return aBooked - bBooked;
            case 'showAsc':
              if (isNaN(aShow) && isNaN(bShow)) return 0;
              if (isNaN(aShow)) return 1; // push missing showDate to end
              if (isNaN(bShow)) return -1;
              return aShow - bShow;
            case 'showDesc':
              if (isNaN(aShow) && isNaN(bShow)) return 0;
              if (isNaN(aShow)) return 1; // push missing showDate to end
              if (isNaN(bShow)) return -1;
              return bShow - aShow;
            case 'bookedDesc':
            default:
              return bBooked - aBooked;
          }
        })
        .map((booking) => (
          <Card key={booking.bookingId}>
            <CardContent className="p-6">
              <div className="space-y-4">
                <div className="flex items-start justify-between">
                  <div>
                    <div className="flex items-center gap-2 mb-1">
                      <h3
                        className="font-medium cursor-pointer hover:text-primary transition-colors"
                        onClick={() => handleViewBookingDetail(booking)}
                      >
                        {booking.performanceTitle ||
                          booking.venueName ||
                          `Schedule #${booking.scheduleId}`}
                      </h3>
                      <Badge variant={getStatusColor(booking.status || '')}>{booking.status}</Badge>
                    </div>
                    {booking.venueName && booking.performanceTitle && (
                      <p className="text-sm text-muted-foreground">{booking.venueName}</p>
                    )}
                    <p className="text-sm text-muted-foreground">
                      Booking: {booking.bookingNumber}
                    </p>
                    {booking.userName && (
                      <p className="text-sm text-muted-foreground">User: {booking.userName}</p>
                    )}
                  </div>
                  <div className="text-right">
                    <p className="font-medium">{formatPrice(booking.totalAmount || 0)}</p>
                    <p className="text-sm text-muted-foreground">
                      {booking.seatCount || 0} seat{(booking.seatCount || 0) > 1 ? 's' : ''}
                    </p>
                  </div>
                </div>

                <div className="grid gap-3 md:grid-cols-2">
                  <div className="flex items-center gap-2 text-sm">
                    <MapPin className="w-4 h-4" />
                    <span>{booking.venueName || `Schedule ID: ${booking.scheduleId}`}</span>
                  </div>

                  <div className="flex items-center gap-2 text-sm">
                    <Calendar className="w-4 h-4" />
                    <span>
                      {(() => {
                        if (booking.status === 'CANCELLED') {
                          const label = booking.cancelledAt
                            ? formatDate(booking.cancelledAt)
                            : 'Unknown';
                          return `Cancelled: ${label}`;
                        }
                        if (booking.showDate) {
                          return `Scheduled: ${formatDate(booking.showDate)}`;
                        }
                        return `Booked: ${formatDate(booking.bookedAt || '')}`;
                      })()}
                    </span>
                  </div>

                  <div className="flex items-start gap-2 text-sm">
                    <Users className="w-4 h-4 mt-0.5" />
                    <div className="flex-1">
                      <div className="mb-1">
                        {booking.seatCount || 0} seat{(booking.seatCount || 0) > 1 ? 's' : ''}
                      </div>
                      <div className="flex flex-wrap gap-1">
                        {(() => {
                          const seatCount = booking.seatCount || 0;
                          // 1) Prefer booking.seats with per-seat zone/rowLabel/colNum
                          if (booking.seats && booking.seats.length > 0) {
                            const labels = booking.seats.map((s: any, i: number) => {
                              const code = `${s?.rowLabel ?? ''}${s?.colNum ?? ''}`.trim();
                              const zone = s?.zone ? String(s.zone) : '';
                              const label = code
                                ? zone
                                  ? `${zone}-${code}`
                                  : code
                                : s?.seatId
                                  ? `Seat ${s.seatId}`
                                  : `Seat ${i + 1}`;
                              return label;
                            });
                            const missing = Math.max(0, seatCount - labels.length);
                            const placeholders = Array.from({ length: missing }).map(
                              (_, i) => `Seat ${labels.length + i + 1}`,
                            );
                            return [...labels, ...placeholders].map((label, idx) => (
                              <Badge key={idx} variant="secondary" className="text-xs">
                                {label}
                              </Badge>
                            ));
                          }
                          // 2) Fallback to locally captured codes
                          const localCodes = localSeatCodesMap[booking.bookingNumber] || [];
                          if (localCodes.length > 0) {
                            const labels = localCodes;
                            const missing = Math.max(0, seatCount - labels.length);
                            const placeholders = Array.from({ length: missing }).map(
                              (_, i) => `Seat ${labels.length + i + 1}`,
                            );
                            return [...labels, ...placeholders].map((label, idx) => (
                              <Badge key={idx} variant="secondary" className="text-xs">
                                {label}
                              </Badge>
                            ));
                          }
                          // 3) Plain placeholders
                          return Array.from({ length: seatCount }).map((_, i) => (
                            <Badge key={i} variant="secondary" className="text-xs">
                              {`Seat ${i + 1}`}
                            </Badge>
                          ));
                        })()}
                      </div>
                    </div>
                  </div>

                  {booking.status === 'CONFIRMED' && (
                    <div className="flex items-center gap-2 text-sm">
                      <CreditCard className="w-4 h-4" />
                      <span>Booked: {formatDate(booking.bookedAt || '')}</span>
                    </div>
                  )}
                </div>

                {bookingDetails && bookingDetails.seats && bookingDetails.seats.length > 0 && (
                  <div className="bg-muted rounded-lg p-3">
                    <p className="text-sm font-medium mb-2">Seat Details:</p>
                    <div className="grid gap-2 text-xs">
                      {bookingDetails.seats.map((seat: any, index: number) => (
                        <div key={index} className="flex justify-between">
                          <span>Seat ID: {seat.seatId}</span>
                          <span>{formatPrice(seat.seatPrice)}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
                {booking.status === 'CANCELLED' && (
                  <Alert>
                    <AlertTriangle className="w-4 h-4" />
                    <AlertDescription>
                      <div className="space-y-1">
                        <p>
                          Booking cancelled on{' '}
                          {booking.cancelledAt ? formatDate(booking.cancelledAt) : 'Unknown'}
                        </p>
                        {booking.cancellationReason && (
                          <p className="text-xs">Reason: {booking.cancellationReason}</p>
                        )}
                      </div>
                    </AlertDescription>
                  </Alert>
                )}

                <div className="flex gap-2 justify-end">
                  {canCancelBooking(booking) && (
                    <Button variant="destructive" onClick={() => handleCancelBooking(booking)}>
                      Cancel Booking
                    </Button>
                  )}

                  {booking.status === 'CONFIRMED' && !canCancelBooking(booking) && (
                    <Button variant="outline" disabled>
                      Cannot Cancel
                    </Button>
                  )}
                  <Button variant="ghost" onClick={() => handleViewBookingDetail(booking)}>
                    View Details
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

      {/* Cancellation Dialog */}
      <Dialog
        open={!!cancellingBooking}
        onOpenChange={(open) => {
          if (!open) {
            setCancellingBooking(null);
            setCancellationReason('');
            setSelectedCancelReason('');
          }
        }}
      >
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Cancel Booking</DialogTitle>
          </DialogHeader>

          {cancellingBooking && (
            <div className="space-y-6">
              {/* Booking Details */}
              <div className="bg-muted rounded-lg p-4">
                <h4 className="font-medium mb-2">Booking Details</h4>
                <div className="space-y-1 text-sm">
                  <p>
                    <strong>Booking Number:</strong> {cancellingBooking.bookingNumber}
                  </p>
                  {cancellingBooking.venueName && (
                    <p>
                      <strong>Venue:</strong> {cancellingBooking.venueName}
                    </p>
                  )}
                  {cancellingBooking.showDate && (
                    <p>
                      <strong>Show Date:</strong> {formatDate(cancellingBooking.showDate)}
                    </p>
                  )}
                  {cancellingBooking.userName && (
                    <p>
                      <strong>User:</strong> {cancellingBooking.userName}
                      {cancellingBooking.userPhone && ` (${cancellingBooking.userPhone})`}
                    </p>
                  )}
                  <p>
                    <strong>Schedule ID:</strong> {cancellingBooking.scheduleId}
                  </p>
                  <p>
                    <strong>Seats:</strong> {cancellingBooking.seatCount} seats
                    {bookingDetails && bookingDetails.seats && (
                      <span>
                        {' '}
                        (IDs: {bookingDetails.seats.map((s: any) => s.seatId).join(', ')})
                      </span>
                    )}
                  </p>
                  <p>
                    <strong>Amount:</strong> {formatPrice(cancellingBooking.totalAmount || 0)}
                  </p>
                </div>
              </div>

              {/* Refund Information - Commented out as requested, uncomment when needed */}
              {/*
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                <h4 className="font-medium mb-2 flex items-center gap-2">
                  <AlertTriangle className="w-4 h-4 text-yellow-600" />
                  Refund Policy
                </h4>
                <div className="space-y-1 text-sm text-yellow-800">
                  <p>• More than 48 hours: 100% refund</p>
                  <p>• 24-48 hours: 80% refund (20% cancellation fee)</p>
                  <p>• 12-24 hours: 50% refund (50% cancellation fee)</p>
                  <p>• Less than 12 hours: No refund</p>
                </div>
                <div className="mt-3 p-2 bg-white rounded border">
                  <p className="text-sm">
                    <strong>Your refund amount: {formatPrice(calculateRefundAmount(cancellingBooking))}</strong>
                    {getCancellationFee(cancellingBooking) > 0 && (
                      <span className="text-red-600 ml-2">
                        ({getCancellationFee(cancellingBooking)}% cancellation fee)
                      </span>
                    )}
                  </p>
                </div>
              </div>
              */}

              {/* Cancellation Reason */}
              <div className="space-y-3">
                <Label htmlFor="cancel-reason">Reason for Cancellation</Label>
                <Select value={selectedCancelReason} onValueChange={setSelectedCancelReason}>
                  <SelectTrigger>
                    <SelectValue placeholder="Please select a reason" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="personal_emergency">Personal Emergency</SelectItem>
                    <SelectItem value="illness">Illness</SelectItem>
                    <SelectItem value="schedule_conflict">Schedule Conflict</SelectItem>
                    <SelectItem value="travel_issues">Travel Issues</SelectItem>
                    <SelectItem value="event_concerns">Event-related Concerns</SelectItem>
                    <SelectItem value="financial_reasons">Financial Reasons</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>

                {selectedCancelReason === 'other' && (
                  <div className="space-y-2">
                    <Label htmlFor="custom-reason">Please specify</Label>
                    <Textarea
                      id="custom-reason"
                      value={cancellationReason}
                      onChange={(e) => setCancellationReason(e.target.value)}
                      placeholder="Please provide details about your cancellation reason..."
                      rows={3}
                    />
                  </div>
                )}
              </div>

              {/* Terms and Conditions - Commented out as requested, uncomment when needed */}
              {/*
              <div className="text-xs text-muted-foreground space-y-1">
                <p>By cancelling this booking, you agree to:</p>
                <ul className="list-disc list-inside space-y-1 ml-2">
                  <li>The refund policy terms stated above</li>
                  <li>Refund processing may take 5-7 business days</li>
                  <li>Refunds will be processed to your original payment method</li>
                  <li>This action cannot be undone</li>
                </ul>
              </div>
              */}

              {/* Action Buttons */}
              <div className="flex gap-3 justify-end">
                <Button
                  variant="outline"
                  onClick={() => setCancellingBooking(null)}
                  disabled={processingCancellation}
                >
                  Keep Booking
                </Button>
                <Button
                  variant="destructive"
                  onClick={processCancellation}
                  disabled={
                    !selectedCancelReason ||
                    processingCancellation ||
                    (selectedCancelReason === 'other' && !cancellationReason.trim())
                  }
                >
                  {processingCancellation ? 'Processing...' : 'Confirm Cancellation'}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Booking Detail View Dialog */}
      <Dialog
        open={!!viewingBooking}
        onOpenChange={(open) => {
          if (!open) {
            setViewingBooking(null);
            setViewingBookingDetails(null);
          }
        }}
      >
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Booking Details</DialogTitle>
          </DialogHeader>

          {viewingBooking && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Booking Number</p>
                  <p className="font-medium">{viewingBooking.bookingNumber}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Status</p>
                  <Badge variant={getStatusColor(viewingBooking.status || '')}>
                    {viewingBooking.status}
                  </Badge>
                </div>

                {viewingBooking.performanceTitle && (
                  <div>
                    <p className="text-sm text-muted-foreground">Performance</p>
                    <p className="font-medium">{viewingBooking.performanceTitle}</p>
                  </div>
                )}

                {viewingBooking.venueName && (
                  <div>
                    <p className="text-sm text-muted-foreground">Venue</p>
                    <p className="font-medium">{viewingBooking.venueName}</p>
                  </div>
                )}

                {viewingBooking.showDate && (
                  <div>
                    <p className="text-sm text-muted-foreground">Show Date</p>
                    <p className="font-medium">{formatDate(viewingBooking.showDate)}</p>
                  </div>
                )}

                <div>
                  <p className="text-sm text-muted-foreground">
                    Seats ({viewingBooking.seatCount || 0})
                  </p>
                  <div className="flex flex-wrap gap-1 mt-1">
                    {(() => {
                      const seatCount = viewingBooking.seatCount || 0;
                      // 1) Prefer viewingBooking.seats with per-seat info
                      if (viewingBooking.seats && viewingBooking.seats.length > 0) {
                        const labels = (viewingBooking.seats as any[]).map((s: any, i: number) => {
                          const code = `${s?.rowLabel ?? ''}${s?.colNum ?? ''}`.trim();
                          const zone = s?.zone ? String(s.zone) : '';
                          const label = code
                            ? zone
                              ? `${zone}-${code}`
                              : code
                            : s?.seatId
                              ? `Seat ${s.seatId}`
                              : `Seat ${i + 1}`;
                          return label;
                        });
                        const missing = Math.max(0, seatCount - labels.length);
                        const placeholders = Array.from({ length: missing }).map(
                          (_, i) => `Seat ${labels.length + i + 1}`,
                        );
                        return [...labels, ...placeholders].map((label, idx) => (
                          <Badge key={idx} variant="secondary" className="text-xs">
                            {label}
                          </Badge>
                        ));
                      }
                      // 2) Local captured codes
                      const localCodes = localSeatCodesMap[viewingBooking.bookingNumber] || [];
                      if (localCodes.length > 0) {
                        const labels = localCodes;
                        const missing = Math.max(0, seatCount - labels.length);
                        const placeholders = Array.from({ length: missing }).map(
                          (_, i) => `Seat ${labels.length + i + 1}`,
                        );
                        return [...labels, ...placeholders].map((label, idx) => (
                          <Badge key={idx} variant="secondary" className="text-xs">
                            {label}
                          </Badge>
                        ));
                      }
                      // 3) Plain placeholders
                      return Array.from({ length: seatCount }).map((_, i) => (
                        <Badge key={i} variant="secondary" className="text-xs">
                          {`Seat ${i + 1}`}
                        </Badge>
                      ));
                    })()}
                  </div>
                </div>

                {viewingBooking.userName && (
                  <div>
                    <p className="text-sm text-muted-foreground">User</p>
                    <p className="font-medium">
                      {viewingBooking.userName}
                      {viewingBooking.userPhone && (
                        <span className="text-muted-foreground"> ({viewingBooking.userPhone})</span>
                      )}
                    </p>
                  </div>
                )}

                {viewingBooking.status === 'CONFIRMED' && (
                  <div>
                    <p className="text-sm text-muted-foreground">Booked At</p>
                    <p className="font-medium">{formatDate(viewingBooking.bookedAt || '')}</p>
                  </div>
                )}
              </div>

              {viewingBookingDetails &&
                viewingBookingDetails.seats &&
                viewingBookingDetails.seats.length > 0 && (
                  <div>
                    <p className="text-sm text-muted-foreground mb-2">Seat Details</p>
                    <div className="bg-muted rounded-lg p-3">
                      {viewingBookingDetails.seats.map((seat: any, index: number) => (
                        <div key={index} className="flex justify-between text-sm">
                          <span>Seat ID: {seat.seatId}</span>
                          <span>{formatPrice(seat.seatPrice)}</span>
                        </div>
                      ))}
                      <div className="border-t mt-2 pt-2 flex justify-between font-medium">
                        <span>Total</span>
                        <span>{formatPrice(viewingBooking.totalAmount || 0)}</span>
                      </div>
                    </div>
                  </div>
                )}
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Card, CardHeader, CardContent, CardTitle, Button, Badge } from '@packages/design-system';

import { ArrowLeft, Calendar, Clock, MapPin, Users, Star, Info, Ticket } from 'lucide-react';
import type { Performance, PerformanceSchedule } from '@/entities/performance';
import { performanceService } from '@/entities/performance/api/performanceService';
import { QueuePopup } from '@/widgets/queue-popup';
import { commonConfig } from '@shared';
import { useClientAuth } from '@/app/providers/ClientAuthProvider';

interface PerformanceDetailProps {
  performance?: Performance;
  onBack?: () => void;
  onBookNow?: (performance: Performance, schedule?: PerformanceSchedule) => void;
}

export default function PerformanceDetailPage({
  performance: propPerformance,
  onBack,
  onBookNow,
}: PerformanceDetailProps) {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useClientAuth();
  const [performance, setPerformance] = useState<Performance | null>(propPerformance || null);
  const [loading, setLoading] = useState(!propPerformance);
  const [selectedSchedule, setSelectedSchedule] = useState<PerformanceSchedule | null>(null);
  const [queueSchedule, setQueueSchedule] = useState<PerformanceSchedule | null>(null);
  const [isQueuePopupOpen, setQueuePopupOpen] = useState(false);

  useEffect(() => {
    if (!propPerformance && id) {
      const fetchPerformance = async () => {
        try {
          const data = await performanceService.getPerformanceById(Number(id));
          setPerformance(data);
        } catch (error) {
          console.error('Failed to fetch performance:', error);
        } finally {
          setLoading(false);
        }
      };
      fetchPerformance();
    }
  }, [id, propPerformance]);

  if (loading) {
    return <div className="container mx-auto px-4 py-8">Loading...</div>;
  }

  if (!performance) {
    return <div className="container mx-auto px-4 py-8">공연 정보를 찾을 수 없습니다.</div>;
  }

  const totalSeats =
    performance.schedules?.reduce(
      (sum: number, schedule: PerformanceSchedule) => sum + (schedule.total_seats || 0),
      0,
    ) || 0;

  const availableSeats =
    performance.schedules?.reduce(
      (sum: number, schedule: PerformanceSchedule) => sum + (schedule.available_seats || 0),
      0,
    ) || 0;

  const bookedSeats = totalSeats - availableSeats;
  const occupancyRate = totalSeats > 0 ? (bookedSeats / totalSeats) * 100 : 0;

  const formatDate = (dateString: string) => {
    if (!dateString) return '날짜 미정';
    try {
      return new Date(dateString).toLocaleDateString('ko-KR', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      });
    } catch {
      return '날짜 오류';
    }
  };

  const formatTime = (dateString: string) => {
    if (!dateString) return '시간 미정';
    try {
      return new Date(dateString).toLocaleTimeString('ko-KR', {
        hour: '2-digit',
        minute: '2-digit',
      });
    } catch {
      return '시간 오류';
    }
  };

  const formatPrice = (price: number) => {
    if (!price || price === 0) return '가격 미정';
    return new Intl.NumberFormat('ko-KR').format(price) + '원';
  };

  const getStatusColor = (status: string) => {
    switch (status?.toUpperCase()) {
      case 'OPEN':
        return 'default';
      case 'UPCOMING': // 새로 추가
        return 'default';
      case 'ONGOING':
        return 'default';
      case 'ENDED': // COMPLETED → ENDED
        return 'secondary';
      case 'CLOSED':
        return 'secondary';
      case 'CANCELLED':
        return 'destructive';
      case 'SOLDOUT':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const getStatusLabel = (status: string) => {
    const statusLabels: Record<string, string> = {
      OPEN: 'Available',
      UPCOMING: 'Upcoming', // 새로 추가
      ONGOING: 'In Progress',
      ENDED: 'Ended', // COMPLETED → ENDED
      CLOSED: 'Closed',
      CANCELLED: 'Cancelled',
      SOLDOUT: 'Sold Out',
    };
    return statusLabels[status?.toUpperCase()] || status || 'Unknown';
  };

  const handleBookingClick = async () => {
    if (!performance || !selectedSchedule) {
      return;
    }

    // 1. 클라이언트 측 인증 상태 확인
    if (!user) {
      const loginUrl = `${commonConfig.urls.accounts}${commonConfig.routes.accountsLogin}`;
      const returnUrl = encodeURIComponent(window.location.href);
      window.location.href = `${loginUrl}?returnUrl=${returnUrl}`;
      return;
    }

    // 2. 서버 측 인증 상태 재확인 (토큰 유효성 검증)
    try {
      const response = await fetch(`https://api.ddcn41.com/v2/auth/me`, {
        method: 'GET',
        credentials: 'include', // 쿠키 포함
      });

      if (!response.ok) {
        throw new Error('Auth verification failed');
      }

      const authData = await response.json();

      if (!authData.authenticated) {
        console.warn('[Queue] Server auth check failed:', authData.error || authData.message);

        // 토큰 만료/무효 시 로그인 페이지로 리다이렉트
        const loginUrl = `${commonConfig.urls.accounts}${commonConfig.routes.accountsLogin}`;
        const returnUrl = encodeURIComponent(window.location.href);
        window.location.href = `${loginUrl}?returnUrl=${returnUrl}`;
        return;
      }

      console.log('[Queue] Auth verified, user:', authData.user.username);
    } catch (error) {
      console.error('[Queue] Auth verification error:', error);

      // 네트워크 오류 등으로 인증 확인 실패 시
      const loginUrl = `${commonConfig.urls.accounts}${commonConfig.routes.accountsLogin}`;
      const returnUrl = encodeURIComponent(window.location.href);
      window.location.href = `${loginUrl}?returnUrl=${returnUrl}`;
      return;
    }

    // 3. 인증 확인 완료 후 대기열 진입
    console.log(
      'Starting queue process for performance:',
      performance.performance_id,
      'schedule:',
      selectedSchedule.schedule_id,
    );

    setQueueSchedule(selectedSchedule);
    setQueuePopupOpen(true);
  };

  const handleQueueClose = () => {
    setQueuePopupOpen(false);
    setQueueSchedule(null);
  };

  const handleQueueComplete = (
    queuePerformance: Performance,
    queueSelectedSchedule?: PerformanceSchedule,
  ) => {
    const targetSchedule = queueSelectedSchedule ?? queueSchedule ?? selectedSchedule;

    setQueuePopupOpen(false);
    setQueueSchedule(null);
    if (targetSchedule) {
      setSelectedSchedule(targetSchedule);
    }

    if (onBookNow) {
      onBookNow(queuePerformance, targetSchedule ?? undefined);
      return;
    }

    if (targetSchedule) {
      navigate(
        `/seat-selection/${targetSchedule.schedule_id}?performanceId=${queuePerformance.performance_id}`,
      );
    }
  };

  const handleQueueExpired = () => {
    setQueuePopupOpen(false);
    setQueueSchedule(null);
  };

  console.log('PerformanceDetail - Received performance data:', performance);

  return (
    <>
      <div className="max-w-6xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center gap-4 mb-6">
          <Button variant="ghost" onClick={onBack || (() => navigate('/'))}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Performances
          </Button>
        </div>

        <div className="grid gap-6 lg:grid-cols-3">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Performance Header */}
            <Card>
              <CardContent className="p-6">
                <div className="flex gap-6">
                  <div className="w-48 h-72 flex-shrink-0">
                    <img
                      src={
                        performance.poster_url ||
                        'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'
                      }
                      alt={performance.title}
                      className="w-full h-full object-cover rounded-lg"
                      onError={(e) => {
                        e.currentTarget.src =
                          'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop';
                      }}
                    />
                  </div>
                  <div className="flex-1 space-y-4">
                    <div>
                      <div className="flex items-center gap-2 mb-2">
                        <h1 className="text-3xl font-medium">{performance.title}</h1>
                        <Badge variant={getStatusColor(performance.status)}>
                          {getStatusLabel(performance.status)}
                        </Badge>
                      </div>
                      <p className="text-lg text-muted-foreground">{performance.description}</p>
                    </div>

                    <div className="space-y-3">
                      <div className="flex items-center gap-3">
                        <MapPin className="w-5 h-5 text-muted-foreground" />
                        <span className="font-medium">{performance.venue_name}</span>
                      </div>

                      <div className="flex items-center gap-3">
                        <Ticket className="w-5 h-5 text-muted-foreground" />
                        <span className="font-medium">{formatPrice(performance.base_price)}</span>
                      </div>
                    </div>

                    <div className="flex items-center gap-2">
                      <Badge variant="outline">{performance.theme}</Badge>
                      {performance.theme && <Badge variant="secondary">{performance.theme}</Badge>}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
            {/* Available Schedules */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Calendar className="w-5 h-5" />
                  Available Show Times
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid gap-3 md:grid-cols-2 lg:grid-cols-3">
                  {performance.schedules && performance.schedules.length > 0 ? (
                    performance.schedules.map((schedule: PerformanceSchedule) => (
                      <div
                        key={schedule.schedule_id}
                        className={`p-4 rounded-lg border cursor-pointer transition-all ${
                          selectedSchedule?.schedule_id === schedule.schedule_id
                            ? 'border-primary bg-primary/5'
                            : 'border-border hover:border-primary/50'
                        } ${
                          schedule.available_seats === 0 || schedule.status === 'SOLDOUT'
                            ? 'opacity-60 cursor-not-allowed'
                            : ''
                        }`}
                        onClick={() => {
                          if (schedule.available_seats > 0 && schedule.status !== 'SOLDOUT') {
                            setSelectedSchedule(schedule);
                          }
                        }}
                      >
                        <div className="space-y-2">
                          <div className="flex items-center justify-between">
                            <div className="space-y-1">
                              <p className="font-medium">{formatDate(schedule.show_datetime)}</p>
                              <p className="text-sm text-muted-foreground">
                                {formatTime(schedule.show_datetime)}
                              </p>
                            </div>
                            <Badge variant={getStatusColor(schedule.status)} className="text-xs">
                              {getStatusLabel(schedule.status)}
                            </Badge>
                          </div>
                          <div className="space-y-1">
                            <div className="flex justify-between text-sm">
                              <span>Price:</span>
                              <span className="font-medium">
                                {formatPrice(performance.base_price)}
                              </span>
                            </div>
                            <div className="flex justify-between text-sm">
                              <span>Available:</span>
                              <span
                                className={
                                  schedule.available_seats > 0 ? 'text-green-600' : 'text-red-600'
                                }
                              >
                                {schedule.available_seats} / {schedule.total_seats}
                              </span>
                            </div>
                          </div>
                          {(schedule.available_seats === 0 || schedule.status === 'SOLDOUT') && (
                            <Badge variant="destructive" className="w-full justify-center">
                              Sold Out
                            </Badge>
                          )}
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="col-span-full text-center py-8 text-muted-foreground">
                      No schedules available for this performance.
                    </div>
                  )}
                </div>
                <p className="text-xs text-muted-foreground mt-3">
                  Select a show time to view details and book tickets for that specific performance.
                </p>
              </CardContent>
            </Card>

            {/* Performance Details */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Info className="w-5 h-5" />
                  Performance Details
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <h3 className="font-medium mb-2">About This Performance</h3>
                  <p className="text-muted-foreground leading-relaxed">
                    {performance.description ||
                      "This is an exciting performance that promises to deliver an unforgettable experience. Join us for an evening of world-class entertainment featuring talented performers and stunning production values. Don't miss this opportunity to be part of something truly special."}
                  </p>
                </div>

                <div className="grid gap-4 md:grid-cols-2">
                  <div>
                    <h4 className="font-medium mb-2">Duration</h4>
                    <p className="text-muted-foreground">
                      {performance.running_time > 0
                        ? `${performance.running_time} minutes`
                        : '시간 미정'}
                    </p>
                  </div>

                  <div>
                    <h4 className="font-medium mb-2">Theme</h4>
                    <p className="text-muted-foreground">{performance.theme}</p>
                  </div>

                  <div>
                    <h4 className="font-medium mb-2">Status</h4>
                    <Badge variant={getStatusColor(performance.status)}>
                      {getStatusLabel(performance.status)}
                    </Badge>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Venue Information */}
            <Card>
              <CardHeader>
                <CardTitle>Venue Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <h3 className="font-medium mb-2">
                    {performance.venue_name || performance.venue}
                  </h3>
                  <div className="grid gap-2 text-sm text-muted-foreground">
                    {/* 선택된 스케줄이 있으면 해당 스케줄 정보, 없으면 전체 정보 */}
                    {selectedSchedule ? (
                      <>
                        <p>Total Capacity: {selectedSchedule.total_seats} seats</p>
                        <p>Available: {selectedSchedule.available_seats} seats</p>
                        <p>
                          Booked: {selectedSchedule.total_seats - selectedSchedule.available_seats}{' '}
                          seats
                        </p>
                        <p>
                          Occupancy:{' '}
                          {selectedSchedule.total_seats > 0
                            ? (
                                ((selectedSchedule.total_seats - selectedSchedule.available_seats) /
                                  selectedSchedule.total_seats) *
                                100
                              ).toFixed(1)
                            : 0}
                          %
                        </p>
                      </>
                    ) : (
                      <>
                        <p>Total Shows: {performance.schedules?.length || 0}</p>
                        <p>Total Available Seats: {availableSeats} seats</p>
                        <p>Average Occupancy: {occupancyRate.toFixed(1)}%</p>
                      </>
                    )}
                  </div>
                </div>

                {performance.venue_address && (
                  <div>
                    <h4 className="font-medium mb-2">Address</h4>
                    <p className="text-sm text-muted-foreground">{performance.venue_address}</p>
                  </div>
                )}

                <div className="bg-muted rounded-lg p-4">
                  <h4 className="font-medium mb-2">Getting There</h4>
                  <p className="text-sm text-muted-foreground">
                    Please arrive at least 30 minutes before the performance begins. Late arrivals
                    may not be admitted until a suitable break in the performance.
                  </p>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Booking Sidebar - 대기열 로직 추가 */}
          <div className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Book Your Seats</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {/* 선택된 스케줄 정보 표시 */}
                {selectedSchedule ? (
                  <>
                    <div className="text-center">
                      <div className="text-2xl font-medium mb-1">
                        {formatPrice(selectedSchedule.base_price || performance.base_price)}
                      </div>
                      <p className="text-sm text-muted-foreground">per seat</p>
                    </div>

                    <div className="space-y-3">
                      <div className="flex justify-between text-sm">
                        <span>Selected Show:</span>
                        <span className="font-medium">
                          {formatDate(selectedSchedule.show_datetime)}
                        </span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span>Show Time:</span>
                        <span className="font-medium">
                          {formatTime(selectedSchedule.show_datetime)}
                        </span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span>Available Seats:</span>
                        <span
                          className={`font-medium ${
                            selectedSchedule.available_seats > 0 ? 'text-green-600' : 'text-red-600'
                          }`}
                        >
                          {selectedSchedule.available_seats}
                        </span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span>Status:</span>
                        <Badge
                          variant={getStatusColor(selectedSchedule.status)}
                          className="text-xs"
                        >
                          {getStatusLabel(selectedSchedule.status)}
                        </Badge>
                      </div>
                    </div>

                    <Button
                      className="w-full"
                      size="lg"
                      onClick={handleBookingClick}
                      disabled={
                        selectedSchedule.available_seats === 0 ||
                        selectedSchedule.status === 'SOLDOUT' ||
                        selectedSchedule.status === 'COMPLETED' ||
                        selectedSchedule.status === 'CANCELLED'
                      }
                    >
                      {selectedSchedule.available_seats === 0 ||
                      selectedSchedule.status === 'SOLDOUT'
                        ? 'Sold Out'
                        : selectedSchedule.status === 'COMPLETED'
                          ? 'Show Ended'
                          : selectedSchedule.status === 'CANCELLED'
                            ? 'Cancelled'
                            : 'Join Queue'}
                    </Button>
                  </>
                ) : (
                  <>
                    <div className="text-center">
                      <div className="text-2xl font-medium mb-1">
                        {formatPrice(performance.base_price)}
                      </div>
                      <p className="text-sm text-muted-foreground">per seat (starting from)</p>
                    </div>

                    <div className="space-y-3">
                      <div className="text-center py-4 text-muted-foreground">
                        Please select a show time above
                      </div>
                    </div>

                    <Button className="w-full" size="lg" disabled={true} variant="outline">
                      Select Show Time First
                    </Button>
                  </>
                )}

                <p className="text-xs text-muted-foreground text-center">
                  대기열 시스템을 통해 공정한 좌석 선택 기회를 제공합니다
                </p>
              </CardContent>
            </Card>

            {/* Occupancy Indicator - 선택된 스케줄 기준으로 업데이트 */}
            <Card>
              <CardContent className="p-4">
                <h4 className="font-medium mb-3">
                  Seat Availability
                  {selectedSchedule && (
                    <span className="text-sm font-normal text-muted-foreground ml-2">
                      ({formatDate(selectedSchedule.show_datetime)})
                    </span>
                  )}
                </h4>
                <div className="space-y-2">
                  {selectedSchedule ? (
                    <>
                      <div className="flex justify-between text-sm">
                        <span>Booked</span>
                        <span>
                          {selectedSchedule.total_seats - selectedSchedule.available_seats} seats
                        </span>
                      </div>
                      <div className="w-full bg-muted rounded-full h-2">
                        <div
                          className="bg-primary h-2 rounded-full transition-all"
                          style={{
                            width: `${
                              selectedSchedule.total_seats > 0
                                ? ((selectedSchedule.total_seats -
                                    selectedSchedule.available_seats) /
                                    selectedSchedule.total_seats) *
                                  100
                                : 0
                            }%`,
                          }}
                        />
                      </div>
                      <p className="text-xs text-muted-foreground">
                        {selectedSchedule.total_seats > 0
                          ? (
                              ((selectedSchedule.total_seats - selectedSchedule.available_seats) /
                                selectedSchedule.total_seats) *
                              100
                            ).toFixed(1)
                          : 0}
                        % booked
                      </p>
                    </>
                  ) : (
                    <>
                      <div className="flex justify-between text-sm">
                        <span>Total Available</span>
                        <span>{availableSeats} seats</span>
                      </div>
                      <div className="w-full bg-muted rounded-full h-2">
                        <div
                          className="bg-primary h-2 rounded-full transition-all"
                          style={{
                            width: `${occupancyRate}%`,
                          }}
                        />
                      </div>
                      <p className="text-xs text-muted-foreground">
                        Select a show time to see detailed availability
                      </p>
                    </>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      {performance && queueSchedule && (
        <QueuePopup
          isOpen={isQueuePopupOpen}
          performance={performance}
          selectedSchedule={queueSchedule}
          onClose={handleQueueClose}
          onQueueComplete={handleQueueComplete}
          onQueueExpired={handleQueueExpired}
        />
      )}
    </>
  );
}

